        if(cor.gt.dech) then
!hr06     cor=c1e3*sqrt(dq1*dq1+dq2*dq2)
          cor=c1e3*sqrt(dq1**2+dq2**2)                                   !hr06
          if(ncorr.eq.1.or.cor.lt.coro) then
            coro=cor
