            aeg(i,l,ll)=zero
          enddo
        enddo
        if(abs(el(i)).le.pieni) goto 190
        kz1=kz(i)+1
        goto(10,30,90,50,70,80,120,170,180),kz1
        goto 190
!-----------------------------------------------------------------------
!  DRIFTLENGTH
!-----------------------------------------------------------------------
   10   do 20 l=1,2
          aeg(i,l,1)=one
          aeg(i,l,2)=el(i)
          aeg(i,l,3)=zero
   20   aeg(i,l,4)=one
        goto 190
!-----------------------------------------------------------------------
!  RECTANGULAR MAGNET
!  HORIZONTAL
!-----------------------------------------------------------------------
   30   ih=1
   40   fok=el(i)*ed(i)/dpsq
        if(abs(fok).le.pieni) goto 10
        rho=(one/ed(i))*dpsq
        si=sin_rn(fok)
        co=cos_rn(fok)
        aeg(i,ih,1)=one
        aeg(i,ih,2)=rho*si
        aeg(i,ih,3)=zero
        aeg(i,ih,4)=one
!hr06   aeg(i,ih,5)=-rho*(one-co)/dpsq
        aeg(i,ih,5)=((-1d0*rho)*(one-co))/dpsq                           !hr06
!hr06   aeg(i,ih,6)=-two*tan_rn(fok*half)/dpsq
        aeg(i,ih,6)=((-1d0*two)*tan_rn(fok*half))/dpsq                   !hr06
!--VERTIKAL
        ih=ih+1
        if(ih.gt.2) ih=1
        g=tan_rn(fok*half)/rho
        gl=el(i)*g
        aeg(i,ih,1)=one-gl
        aeg(i,ih,2)=el(i)
!hr06   aeg(i,ih,3)=-g*(two-gl)
        aeg(i,ih,3)=(-1d0*g)*(two-gl)                                    !hr06
        aeg(i,ih,4)=aeg(i,ih,1)
        goto 190
!-----------------------------------------------------------------------
!  SEKTORMAGNET
!  HORIZONTAL
!-----------------------------------------------------------------------
   50   ih=1
   60   fok=el(i)*ed(i)/dpsq
        if(abs(fok).le.pieni) goto 10
        rho=(one/ed(i))*dpsq
        si=sin_rn(fok)
        co=cos_rn(fok)
        aeg(i,ih,1)=co
        aeg(i,ih,2)=rho*si
!hr06   aeg(i,ih,3)=-si/rho
        aeg(i,ih,3)=(-1d0*si)/rho                                        !hr06
        aeg(i,ih,4)=co
!hr06   aeg(i,ih,5)=-rho*(one-co)/dpsq
        aeg(i,ih,5)=((-1d0*rho)*(one-co))/dpsq                           !hr06
!hr06   aeg(i,ih,6)=-si/dpsq
        aeg(i,ih,6)=(-1d0*si)/dpsq                                       !hr06
!--VERTIKAL
        ih=ih+1
        if(ih.gt.2) ih=1
        aeg(i,ih,1)=one
        aeg(i,ih,2)=el(i)
        aeg(i,ih,3)=zero
        aeg(i,ih,4)=one
        goto 190
!-----------------------------------------------------------------------
!  RECTANGULAR MAGNET VERTIKAL
!-----------------------------------------------------------------------
   70   ih=2
        goto 40
!-----------------------------------------------------------------------
!  SEKTORMAGNET VERTIKAL
!-----------------------------------------------------------------------
   80   ih=2
        goto 60
!-----------------------------------------------------------------------
!  QUADRUPOLE
!  FOCUSSING
!-----------------------------------------------------------------------
   90   fok=ek(i)/(one+dpp)
        if(abs(fok).le.pieni) goto 10
        ih=0
        hi=sqrt(abs(fok))
        fi=el(i)*hi
        if(fok.gt.zero) goto 110
  100   ih=ih+1
        aeg(i,ih,1)=cos_rn(fi)
        hi1=sin_rn(fi)
        aeg(i,ih,2)=hi1/hi
!hr06   aeg(i,ih,3)=-hi1*hi
        aeg(i,ih,3)=(-1d0*hi1)*hi                                        !hr06
        aeg(i,ih,4)=aeg(i,ih,1)
        if(ih.eq.2) goto 190
!--DEFOCUSSING
  110   ih=ih+1
        hp=exp_rn(fi)
        hm=one/hp
        hc=(hp+hm)*half
        hs=(hp-hm)*half
        aeg(i,ih,1)=hc
        aeg(i,ih,2)=hs/hi
        aeg(i,ih,3)=hs*hi
        aeg(i,ih,4)=hc
        if(ih.eq.1) goto 100
        goto 190
!-----------------------------------------------------------------------
!  COMBINED FUNCTION MAGNET HORIZONTAL
!  FOCUSSING
!-----------------------------------------------------------------------
  120   ih=0
        fokq=ek(i)
  130   wf=ed(i)/dpsq
!hr06   fok=fokq/dpd-wf*wf
        fok=fokq/dpd-wf**2                                               !hr06
        if(abs(fok).le.pieni) goto 10
        afok=abs(fok)
        hi=sqrt(afok)
        fi=hi*el(i)
        if(fok.gt.zero) goto 160
  140   ih=ih+1
        si=sin_rn(fi)
        co=cos_rn(fi)
        aeg(i,ih,1)=co
        aeg(i,ih,2)=si/hi
!hr06   aeg(i,ih,3)=-si*hi
        aeg(i,ih,3)=(-1d0*si)*hi                                         !hr06
        aeg(i,ih,4)=co
!hr06   aeg(i,ih,5)=-wf/afok*(one-co)/dpsq
        aeg(i,ih,5)=(((-1d0*wf)/afok)*(one-co))/dpsq                     !hr06
!hr06   aeg(i,ih,6)=-wf/hi*si/dpsq
        aeg(i,ih,6)=(((-1d0*wf)/hi)*si)/dpsq                             !hr06
        ih=ih+1
        if(ih.gt.2) ih=1
        hi=sqrt(abs(ek(i)/dpd))
        fi=hi*el(i)
        hp=exp_rn(fi)
        hm=one/hp
        hc=(hp+hm)*half
        hs=(hp-hm)*half
        aeg(i,ih,1)=hc
        aeg(i,ih,2)=el(i)
        if(abs(hi).le.pieni) goto 150
        aeg(i,ih,2)=hs/hi
  150   aeg(i,ih,3)=hs*hi
        aeg(i,ih,4)=hc
        goto 190
!--DEFOCUSSING
  160   ih=ih+1
        hp=exp_rn(fi)
        hm=one/hp
        hc=(hp+hm)*half
        hs=(hp-hm)*half
        aeg(i,ih,1)=hc
        aeg(i,ih,2)=hs/hi
        aeg(i,ih,3)=hs*hi
        aeg(i,ih,4)=hc
!hr06   aeg(i,ih,5)= wf/afok*(one-hc)/dpsq
        aeg(i,ih,5)= ((wf/afok)*(one-hc))/dpsq                           !hr06
!hr06   aeg(i,ih,6)=-wf/hi*hs/dpsq
        aeg(i,ih,6)=(((-1d0*wf)/hi)*hs)/dpsq                             !hr06
        ih=ih+1
        if(ih.gt.2) ih=1
        hi=sqrt(abs(ek(i)/dpd))
        fi=hi*el(i)
        si=sin_rn(fi)
        co=cos_rn(fi)
        aeg(i,ih,1)=co
        aeg(i,ih,2)=si/hi
!hr06   aeg(i,ih,3)=-si*hi
        aeg(i,ih,3)=(-1d0*si)*hi                                         !hr06
        aeg(i,ih,4)=co
        goto 190
!-----------------------------------------------------------------------
!  COMBINED FUNCTION MAGNET VERTICAL
!-----------------------------------------------------------------------
  170   ih=1
!hr06   fokq=-ek(i)
        fokq=-1d0*ek(i)                                                  !hr06
        goto 130
!-----------------------------------------------------------------------
!  EDGE FOCUSSING
!-----------------------------------------------------------------------
  180   rhoi=ed(i)/dpsq
!hr06   fok=rhoi*tan_rn(el(i)*rhoi*half)
        fok=rhoi*tan_rn((el(i)*rhoi)*half)                               !hr06
        aeg(i,1,1)=one
        aeg(i,1,2)=zero
        aeg(i,1,3)=fok
        aeg(i,1,4)=one
        aeg(i,2,1)=one
        aeg(i,2,2)=zero
!hr06   aeg(i,2,3)=-fok
        aeg(i,2,3)=-1d0*fok
        aeg(i,2,4)=one
        goto 190
!-----------------------------------------------------------------------
!   NONLINEAR INSERTION
!-----------------------------------------------------------------------
  190 continue
      call blockdis(aeg,bl1eg,bl2eg)
      return
      end
