     &                       fexpr_dynk(nfexpr_dynk)   )
         fexpr_dynk(nfexpr_dynk+1) = round_near(errno, ! b
     &        getfields_lfields(5)+1, getfields_fields(5) )
         if (errno.ne.0)
     &        call rounderr( errno,getfields_fields,5,
     &                       fexpr_dynk(nfexpr_dynk+1) )
