         call dynk_checkargs(getfields_nfields,7,
     &        "FUN funname LINSEG x1 x2 y1 y2" )
         call dynk_checkspace(0,4,1)
       
         ! Set pointers to start of funs data blocks
         nfuncs_dynk = nfuncs_dynk+1
