!hr06       if(-dphi.gt.pieni) dphi=dphi+pi
            if(-1d0*dphi.gt.pieni) dphi=dphi+pi                          !hr06
   90     phi(l)=phi(l)+dphi/pie
          goto 130
!--MAGNETELEMENT
