              dphi=-1d0*phibf(l)                                         !hr06
            endif
!hr06       if(kz(jk).ne.8.and.-dphi.gt.pieni) dphi=dphi+pi
            if(kz(jk).ne.8.and.-1d0*dphi.gt.pieni) dphi=dphi+pi          !hr06
            phi(l)=phi(l)+dphi/pie
          enddo
