        dyy2=ekk*(tilts(k)*crkve-tiltc(k)*cikve)                         !hr02
!hr08   tiltck=tiltc(k)*tiltc(k)-tilts(k)*tilts(k)
        tiltck=tiltc(k)**2-tilts(k)**2                                   !hr08
!hr02   tiltsk=two*tiltc(k)*tilts(k)
        tiltsk=(two*tiltc(k))*tilts(k)                                   !hr02
        qu=ekk*tiltck
!hr08   qv=-ekk*tiltsk
        qv=(-1d0*ekk)*tiltsk                                             !hr08
        goto 420
!--NORMAL SEXTUPOLE
  190   ekk=ekk*c1m3
!hr08   tiltck=tiltc(k)*tiltc(k)-tilts(k)*tilts(k)
        tiltck=tiltc(k)**2-tilts(k)**2                                   !hr08
