      subroutine readd1(user,jaord,jpord)
!-----------------------------------------------------------------------
!---- SUBROUTINE TO READ DATA
!-----------------------------------------------------------------------
      implicit none
+if cr
+ca crcoall
+ei
+if crlibm
+ca crlibco
+ei
      integer icont,ind,j,j1,j2,j3,j4,j5,j6,jaord,jcomp,jel,jord,jpord, &
     &maxcomp,njx,njx1,njz,njz1,nmax,np,ncoef,nord,point,kointer
      real user
      double precision cc
+ca commadha
+ca commadh1
      dimension ind(10),user(500)
+if crlibm
      integer nchars
      parameter (nchars=160)
      character*(nchars) ch
      character*(nchars+nchars) ch1
      integer maxf,nofields
      parameter (maxf=30)
      parameter (nofields=20)
      character*(maxf) fields(nofields)
      integer errno,nfields,nunit,lineno,nf
      double precision fround
      data lineno /0/
+ei
+ca save
!-----------------------------------------------------------------------
      nmax=40
!-----------------------------------------------------------------------
      maxa=jaord+1
      maxp=jpord
      if(jpord.eq.0) then
        maxp=jaord+1
        maxcomp=maxa
      else if(jaord.eq.0) then
        maxa=jpord
        maxcomp=1
      end if
!-----------------------------------------------------------------------
      rewind 23
!-----------------------------------------------------------------------
! Unit 23 is opened with round='nearest' if fio is selected
+if .not.crlibm
   10 read(23,'(I6,2X,G20.14,I5,4X,18(2I2,1X))',end=30) ncoef,cc,nord,  &
     &njx,njx1,njz,njz1,np,(ind(jel),jel=1,jeltot)
      read(23,*,end=30) cc
+ei
+if crlibm
   10 read(23,*,end=30) ch
      ch1(:nchars+3)=ch(:nchars)//' / '
      lineno=lineno+1
      call splitfld(errno,23,lineno,nofields,nf,ch1,fields)
      if (nf.gt.0) then
        read (fields(1),*) ncoef
        nf=nf-1
      endif
      if (nf.gt.0) then
        cc=fround(errno,fields,2)
        nf=nf-1
      endif
      if (nf.gt.0) then
        read (fields(3),*) nord
        nf=nf-1
      endif
      if (nf.gt.0) then
        read (fields(4),*) njx
        nf=nf-1
      endif
      if (nf.gt.0) then
        read (fields(5),*) njx1
        nf=nf-1
      endif
      if (nf.gt.0) then
        read (fields(6),*) njz
        nf=nf-1
      endif
      if (nf.gt.0) then
        read (fields(7),*) njz1
        nf=nf-1
      endif
      if (nf.gt.0) then
        read (fields(8),*) np
        nf=nf-1
      endif
      do jel=1,jeltot
        if (nf.gt.0) then
          read (fields(8+jel),*) ind(jel)
          nf=nf-1
        endif
      enddo
      read(23,*,end=30) ch
      lineno=lineno+1
      ch1(:nchars+3)=ch(:nchars)//' / '
      call splitfld(errno,23,lineno,nofields,nf,ch1,fields)
      if (nf.gt.0) then
        cc=fround(errno,fields,1)
        nf=nf-1
      endif
+ei
!-----------------------------------------------------------------------
!---- CODING IND IN BASE 3
!-----------------------------------------------------------------------
      if(njx.eq.njx1.and.njz.eq.njz1.and.(njx+njz).le.maxa.and. np.le.  &
     &maxp) then
        point=0
        do 20 j=1,jeltot
          point=point+ind(j)*3**(j-1)
   20   continue
        if(point.gt.4000) then
+if cr
          write(lout,10000)
+ei
+if .not.cr
          write(*,10000)
+ei
          call closeUnits
+if cr
      call abend('Problem with data in fort.23')
+ei
+if .not.cr
          stop
+ei
        end if
!-----------------------------------------------------------------------
!---- DATA PROCESSING
!-----------------------------------------------------------------------
        hda(njx,njx+njz,np,point)=cc+hda(njx,njx+njz,np,point)
!-----------------------------------------------------------------------
      end if
!-----------------------------------------------------------------------
      goto 10
!-----------------------------------------------------------------------
!---- DEFINES DATA FOR THE ROUTINE OBJFUN
!-----------------------------------------------------------------------
   30 if(jeltot.eq.1) then
!-----------------------------------------------------------------------
        do 50 jcomp=0,maxcomp
          icont=0
          do 40 jord=0,2
            j1=jord
!-----------------------------------------------------------------------
            icont=icont+1
            kointer=j1
!-----------------------------------------------------------------------
            user(jcomp*nmax+icont)=hda(jcomp,jaord+1,jpord,kointer)
   40     continue
   50   continue
!-----------------------------------------------------------------------
      else if(jeltot.eq.2) then
!-----------------------------------------------------------------------
        do 80 jcomp=0,maxcomp
          icont=0
          do 70 jord=0,2
            do 60 j1=0,jord
              j2=jord-j1
!-----------------------------------------------------------------------
              icont=icont+1
              kointer=j1+j2*3
!-----------------------------------------------------------------------
              user(jcomp*nmax+icont)=hda(jcomp,jaord+1,jpord,kointer)
   60       continue
   70     continue
   80   continue
!-----------------------------------------------------------------------
      else if(jeltot.eq.3) then
!-----------------------------------------------------------------------
        do 120 jcomp=0,maxcomp
          icont=0
          do 110 jord=0,2
            do 100 j1=0,jord
              do 90 j2=0,jord-j1
                j3=jord-j1-j2
!-----------------------------------------------------------------------
                icont=icont+1
!hr04           kointer=j1+j2*3+j3*3*3
                kointer=(j1+j2*3)+j3*3**2                                !hr04
!-----------------------------------------------------------------------
                user(jcomp*nmax+icont)=hda(jcomp,jaord+1,jpord, kointer)
   90         continue
  100       continue
  110     continue
  120   continue
!-----------------------------------------------------------------------
      else if(jeltot.eq.4) then
!-----------------------------------------------------------------------
        do 170 jcomp=0,maxcomp
          icont=0
          do 160 jord=0,2
            do 150 j1=0,jord
              do 140 j2=0,jord-j1
                do 130 j3=0,jord-j1-j2
                  j4=jord-j1-j2-j3
!-----------------------------------------------------------------------
                  icont=icont+1
!hr04             kointer=j1+j2*3+j3*3*3+j4*3*3*3
                  kointer=((j1+j2*3)+j3*3**2)+j4*3**3                    !hr04
!-----------------------------------------------------------------------
                  user(jcomp*nmax+icont)=hda(jcomp,jaord+1,jpord,       &
     &kointer)
  130           continue
  140         continue
  150       continue
  160     continue
  170   continue
!-----------------------------------------------------------------------
      else if(jeltot.eq.5) then
!-----------------------------------------------------------------------
        do 230 jcomp=0,maxcomp
          icont=0
          do 220 jord=0,2
            do 210 j1=0,jord
              do 200 j2=0,jord-j1
                do 190 j3=0,jord-j1-j2
                  do 180 j4=0,jord-j1-j2-j3
                    j5=jord-j1-j2-j3-j4
!-----------------------------------------------------------------------
                    icont=icont+1
!hr04               kointer=j1+j2*3+j3*3*3+j4*3*3*3+j5*3**4
                    kointer=(((j1+j2*3)+j3*3**2)+j4*3**3)+j5*3**4        !hr04
!-----------------------------------------------------------------------
                    user(jcomp*nmax+icont)=hda(jcomp,jaord+1, jpord,    &
     &kointer)
  180             continue
  190           continue
  200         continue
  210       continue
  220     continue
  230   continue
!-----------------------------------------------------------------------
      else if(jeltot.eq.6) then
!-----------------------------------------------------------------------
        do 300 jcomp=0,maxcomp
          icont=0
          do 290 jord=0,2
            do 280 j1=0,jord
              do 270 j2=0,jord-j1
                do 260 j3=0,jord-j1-j2
                  do 250 j4=0,jord-j1-j2-j3
                    do 240 j5=0,jord-j1-j2-j3-j4
                      j6=jord-j1-j2-j3-j4-j5
!-----------------------------------------------------------------------
                      icont=icont+1
!hr04                 kointer=j1+j2*3+j3*3*3+j4*3*3*3+j5*3**4 +j6*3**5
                 kointer=((((j1+j2*3)+j3*3**2)+j4*3**3)+j5*3**4)+j6*3**5 !hr04
!-----------------------------------------------------------------------
                      user(jcomp*nmax+icont)=hda(jcomp,jaord+1, jpord,  &
     &kointer)
  240               continue
  250             continue
  260           continue
  270         continue
  280       continue
  290     continue
  300   continue
!-----------------------------------------------------------------------
      end if
!-----------------------------------------------------------------------
!-----------------------------------------------------------------------
      return
10000 format(//,t10,' INDEX OUT OF BOUND IN ROUTINE READD ')
      end
      subroutine hamilton1(ja,jp)
!-----------------------------------------------------------------------
!---- COMPUTES THE VALUE OF THE HAMILTONIAN AFTER CORRECTIONS
!-----------------------------------------------------------------------
      implicit none
+if cr
+ca crcoall
+ei
+if crlibm
+ca crlibco
+ei
      integer j1,j2,j3,j4,j5,j6,ja,jcomp,jel,jord,jp,l,ncoef,kointer
      double precision tham
      dimension tham(0:3)
+ca commadha
+ca commadh1
+ca save
!-----------------------------------------------------------------------
      do 10 jcomp=0,3
        tham(jcomp)=0d0
   10 continue
!-----------------------------------------------------------------------
      if(jp.eq.0) then
        ncoef=ja
      else
        ncoef=1
      end if
!-----------------------------------------------------------------------
      goto(20,50,90,140,200,270) jeltot
      goto 350
!-----------------------------------------------------------------------
   20 do 40 jord=0,2
        j1=jord
!-----------------------------------------------------------------------
        kointer=j1
!-----------------------------------------------------------------------
        do 30 l=0,ncoef
          tham(l)=tham(l)+hda(l,ja,jp,kointer)*(x(1)**j1)
   30   continue
   40 continue
!-----------------------------------------------------------------------
      goto 350
!-----------------------------------------------------------------------
   50 do 80 jord=0,2
        do 70 j1=0,jord
          j2=jord-j1
!-----------------------------------------------------------------------
          kointer=j1+j2*3
!-----------------------------------------------------------------------
          do 60 l=0,ncoef
!hr04       tham(l)=tham(l)+hda(l,ja,jp,kointer)*(x(1)**j1) *(x(2)**j2)
            tham(l)=tham(l)+(hda(l,ja,jp,kointer)*(x(1)**j1))*(x(2)**j2) !hr04
   60     continue
   70   continue
   80 continue
!-----------------------------------------------------------------------
      goto 350
!-----------------------------------------------------------------------
   90 do 130 jord=0,2
        do 120 j1=0,jord
          do 110 j2=0,jord-j1
            j3=jord-j1-j2
!-----------------------------------------------------------------------
!hr04       kointer=j1+j2*3+j3*3**2
            kointer=(j1+j2*3)+j3*3**2                                    !hr04
!-----------------------------------------------------------------------
            do 100 l=0,ncoef
!hr04         tham(l)=tham(l)+hda(l,ja,jp,kointer)*(x(1)**j1)           &
!hr04&*(x(2)**j2)*(x(3)**j3)
              tham(l)=tham(l)+((hda(l,ja,jp,kointer)*(x(1)**j1))        &!hr04
     &*(x(2)**j2))*(x(3)**j3)                                            !hr04
  100       continue
  110     continue
  120   continue
  130 continue
!-----------------------------------------------------------------------
      goto 350
!-----------------------------------------------------------------------
  140 do 190 jord=0,2
        do 180 j1=0,jord
          do 170 j2=0,jord-j1
            do 160 j3=0,jord-j1-j2
              j4=jord-j1-j2-j3
!-----------------------------------------------------------------------
!hr04         kointer=j1+j2*3+j3*3**2+j4*3**3
              kointer=((j1+j2*3)+j3*3**2)+j4*3**3                        !hr04
!-----------------------------------------------------------------------
              do 150 l=0,ncoef
!hr04           tham(l)=tham(l)+hda(l,ja,jp,kointer)*(x(1)**j1)         &
!hr04&*(x(2)**j2)*(x(3)**j3)*(x(4)**j4)
                tham(l)=tham(l)+(((hda(l,ja,jp,kointer)*(x(1)**j1))     &!hr04
     &*(x(2)**j2))*(x(3)**j3))*(x(4)**j4)                                !hr04
  150         continue
  160       continue
  170     continue
  180   continue
  190 continue
!-----------------------------------------------------------------------
      goto 350
!-----------------------------------------------------------------------
  200 do 260 jord=0,2
        do 250 j1=0,jord
          do 240 j2=0,jord-j1
            do 230 j3=0,jord-j1-j2
              do 220 j4=0,jord-j1-j2-j3
                j5=jord-j1-j2-j3-j4
!-----------------------------------------------------------------------
!hr04           kointer=j1+j2*3+j3*3**2+j4*3**3+j5*3**4
                kointer=(((j1+j2*3)+j3*3**2)+j4*3**3)+j5*3**4            !hr04
!-----------------------------------------------------------------------
                do 210 l=0,ncoef
!hr04             tham(l)=tham(l)+hda(l,ja,jp,kointer)*(x(1)**j1)       &
!hr04&*(x(2)**j2)*(x(3)**j3)*(x(4)**j4) *(x(5)**j5)
                  tham(l)=tham(l)+((((hda(l,ja,jp,kointer)*(x(1)**j1))  &!hr04
     &*(x(2)**j2))*(x(3)**j3))*(x(4)**j4))*(x(5)**j5)                    !hr04
  210           continue
  220         continue
  230       continue
  240     continue
  250   continue
  260 continue
!-----------------------------------------------------------------------
      goto 350
!-----------------------------------------------------------------------
  270 do 340 jord=0,2
        do 330 j1=0,jord
          do 320 j2=0,jord-j1
            do 310 j3=0,jord-j1-j2
              do 300 j4=0,jord-j1-j2-j3
                do 290 j5=0,jord-j1-j2-j3-j4
                  j6=jord-j1-j2-j3-j4-j5
!-----------------------------------------------------------------------
!hr04             kointer=j1+j2*3+j3*3**2+j4*3**3+j5*3**4 +j6*3**5
                 kointer=((((j1+j2*3)+j3*3**2)+j4*3**3)+j5*3**4)+j6*3**5 !hr04
!-----------------------------------------------------------------------
                  do 280 l=0,ncoef
!hr04               tham(l)=tham(l)+hda(l,ja,jp,kointer) *(x(1)**j1)    &
!hr04&*(x(2)**j2)*(x(3)**j3) *(x(4)**j4)*(x(5)**j5)                     &
!hr04&*(x(6)**j6)
                 tham(l)=tham(l)+(((((hda(l,ja,jp,kointer) *(x(1)**j1)) &!hr04
     &*(x(2)**j2))*(x(3)**j3))*(x(4)**j4))*(x(5)**j5))                  &!hr04
     &*(x(6)**j6)                                                        !hr04
  280             continue
  290           continue
  300         continue
  310       continue
  320     continue
  330   continue
  340 continue
!-----------------------------------------------------------------------
  350 do 360 jel=0,ncoef
        ham(jel)=tham(jel)
  360 continue
!-----------------------------------------------------------------------
      return
!-----------------------------------------------------------------------
      end
      subroutine objfun1(mode,n,x,objf,objgrd,nstate,iuser,user)
!-----------------------------------------------------------------------
!---- ROUTINE TO COMPUTE THE VALUE OF THE FUNCTION AND OF ITS
!---- DERIVATIVES
!-----------------------------------------------------------------------
      implicit none
+if cr
+ca crcoall
+ei
+if crlibm
+ca crlibco
+ei
      integer icont,iuser,j1,j2,j3,j4,j5,j6,jel,jord,jvar,l,mode,n,nmax,&
     &nstate,kointer
      real user
      double precision fder,fun,objf,objgrd,x
      dimension iuser(*),x(10),objgrd(10),user(*),fun(0:3),fder(0:3,10)
+ca save
!-----------------------------------------------------------------------
      nmax=40
!-----------------------------------------------------------------------
      do 20 jel=0,3
        do 10 jvar=1,n
          fder(jel,jvar)=0d0
   10   continue
        fun(jel)=0d0
   20 continue
!-----------------------------------------------------------------------
      if(n.eq.1) then
!-----------------------------------------------------------------------
        do 40 l=0,iuser(3)
          icont=0
          do 30 jord=0,2
            j1=jord
!-----------------------------------------------------------------------
            kointer=j1
            icont=icont+1
!-----------------------------------------------------------------------
            fun(l)=fun(l)+user(l*nmax+icont)*(x(1)**j1)
!hr04       fder(l,1)=fder(l,1)+user(l*nmax+icont)*dble(j1) *(x(1)      &
!hr04&**(j1-1))
            fder(l,1)=fder(l,1)+(user(l*nmax+icont)*dble(j1))*(x(1)     &!hr04
     &**(j1-1))                                                          !hr04
   30     continue
   40   continue
!-----------------------------------------------------------------------
      else if(n.eq.2) then
!-----------------------------------------------------------------------
        do 70 l=0,iuser(3)
          icont=0
          do 60 jord=0,2
            do 50 j1=0,jord
              j2=jord-j1
!-----------------------------------------------------------------------
              kointer=j1+j2*3
              icont=icont+1
!-----------------------------------------------------------------------
!hr04         fun(l)=fun(l)+user(l*nmax+icont)*(x(1)**j1) *(x(2)**j2)
              fun(l)=fun(l)+(user(l*nmax+icont)*(x(1)**j1))*(x(2)**j2)
!hr04         fder(l,1)=fder(l,1)+user(l*nmax+icont)*dble(j1) *(x(1)    &
!hr04&**(j1-1))*(x(2)**j2)
              fder(l,1)=fder(l,1)+((user(l*nmax+icont)*dble(j1))*(x(1)  &!hr04
     &**(j1-1)))*(x(2)**j2)                                              !hr04
!hr04         fder(l,2)=fder(l,2)+user(l*nmax+icont)*dble(j2)           &
!hr04&*(x(1)**j1)*(x(2)**(j2-1))
              fder(l,2)=fder(l,2)+((user(l*nmax+icont)*dble(j2))        &!hr04
     &*(x(1)**j1))*(x(2)**(j2-1))                                        !hr04
   50       continue
   60     continue
   70   continue
!-----------------------------------------------------------------------
      else if(n.eq.3) then
!-----------------------------------------------------------------------
        do 110 l=0,iuser(3)
          icont=0
          do 100 jord=0,2
            do 90 j1=0,jord
              do 80 j2=0,jord-j1
                j3=jord-j1-j2
!-----------------------------------------------------------------------
!hr04           kointer=j1+j2*3+j3*3**2
                kointer=(j1+j2*3)+j3*3**2                                !hr04
                icont=icont+1
!-----------------------------------------------------------------------
!hr04           fun(l)=fun(l)+user(l*nmax+icont)*(x(1)**j1) *(x(2)**j2) &
!hr04&*(x(3)**j3)
              fun(l)=fun(l)+((user(l*nmax+icont)*(x(1)**j1))*(x(2)**j2))&!hr04
     &*(x(3)**j3)                                                        !hr04
!hr04           fder(l,1)=fder(l,1)+user(l*nmax+icont)*dble(j1) *(x(1)  &
!hr04&**(j1-1))*(x(2)**j2)*(x(3)**j3)
              fder(l,1)=fder(l,1)+(((user(l*nmax+icont)*dble(j1))*(x(1) &!hr04
     &**(j1-1)))*(x(2)**j2))*(x(3)**j3)                                  !hr04
!hr04           fder(l,2)=fder(l,2)+user(l*nmax+icont)*dble(j2)         &
!hr04&*(x(1)**j1)*(x(2)**(j2-1))*(x(3)**j3)
                fder(l,2)=fder(l,2)+(((user(l*nmax+icont)*dble(j2))     &!hr04
     &*(x(1)**j1))*(x(2)**(j2-1)))*(x(3)**j3)                            !hr04
!hr04           fder(l,3)=fder(l,3)+user(l*nmax+icont)*dble(j3)         &
!hr04&*(x(1)**j1)*(x(2)**j2)*(x(3)**(j3-1))
                fder(l,3)=fder(l,3)+(((user(l*nmax+icont)*dble(j3))     &!hr04
     &*(x(1)**j1))*(x(2)**j2))*(x(3)**(j3-1))                            !hr04
   80         continue
   90       continue
  100     continue
  110   continue
!-----------------------------------------------------------------------
      else if(n.eq.4) then
!-----------------------------------------------------------------------
        do 160 l=0,iuser(3)
          icont=0
          do 150 jord=0,2
            do 140 j1=0,jord
              do 130 j2=0,jord-j1
                do 120 j3=0,jord-j1-j2
                  j4=jord-j1-j2-j3
!-----------------------------------------------------------------------
                  kointer=j1+j2*3+j3*3**2+j4*3**3
                  icont=icont+1
!-----------------------------------------------------------------------
!hr04             fun(l)=fun(l)+user(l*nmax+icont)*(x(1)**j1)           &
!hr04&*(x(2)**j2)*(x(3)**j3)*(x(4)**j4)
                  fun(l)=fun(l)+(((user(l*nmax+icont)*(x(1)**j1))       &!hr04
     &*(x(2)**j2))*(x(3)**j3))*(x(4)**j4)                                !hr04
!hr04             fder(l,1)=fder(l,1)+user(l*nmax+icont)*dble(j1)*(x    &
!hr04&(1)**(j1-1))*(x(2)**j2)*(x(3 )**j3)*(x(4)**j4)
                fder(l,1)=fder(l,1)+((((user(l*nmax+icont)*dble(j1))*(x &!hr04
     &(1)**(j1-1)))*(x(2)**j2))*(x(3 )**j3))*(x(4)**j4)                  !hr04
!hr04             fder(l,2)=fder(l,2)+user(l*nmax+icont)*dble(j2)       &
!hr04&*(x(1)**j1)*(x(2)**(j2-1))*(x(3 )**j3)*(x(4)**j4)
                  fder(l,2)=fder(l,2)+((((user(l*nmax+icont)*dble(j2))  &!hr04
     &*(x(1)**j1))*(x(2)**(j2-1)))*(x(3 )**j3))*(x(4)**j4)               !hr04
!hr04             fder(l,3)=fder(l,3)+user(l*nmax+icont)*dble(j3)       &
!hr04&*(x(1)**j1)*(x(2)**j2)*(x(3 )**(j3-1))*(x(4)**j4)
                  fder(l,3)=fder(l,3)+((((user(l*nmax+icont)*dble(j3))  &!hr04
     &*(x(1)**j1))*(x(2)**j2))*(x(3 )**(j3-1)))*(x(4)**j4)               !hr04
!hr04             fder(l,4)=fder(l,4)+user(l*nmax+icont)*dble(j4)       &
!hr04&*(x(1)**j1)*(x(2)**j2)*(x(3 )**j3)*(x(4)**(j4-1))
                  fder(l,4)=fder(l,4)+((((user(l*nmax+icont)*dble(j4))  &!hr04
     &*(x(1)**j1))*(x(2)**j2))*(x(3 )**j3))*(x(4)**(j4-1))               !hr04
  120           continue
  130         continue
  140       continue
  150     continue
  160   continue
!-----------------------------------------------------------------------
      else if(n.eq.5) then
!-----------------------------------------------------------------------
        do 220 l=0,iuser(3)
          icont=0
          do 210 jord=0,2
            do 200 j1=0,jord
              do 190 j2=0,jord-j1
                do 180 j3=0,jord-j1-j2
                  do 170 j4=0,jord-j1-j2-j3
                    j5=jord-j1-j2-j3-j4
!-----------------------------------------------------------------------
!hr04               kointer=j1+j2*3+j3*3**2+j4*3**3+j5*3**4
                    kointer=(((j1+j2*3)+j3*3**2)+j4*3**3)+j5*3**4        !hr04
                    icont=icont+1
!-----------------------------------------------------------------------
!hr04               fun(l)=fun(l)+user(l*nmax+icont)*(x(1)**j1)         &
!hr04&*(x(2)**j2)*(x(3)**j3)*(x(4)**j4) *(x(5)**j5)
                    fun(l)=fun(l)+((((user(l*nmax+icont)*(x(1)**j1))    &!hr04
     &*(x(2)**j2))*(x(3)**j3))*(x(4)**j4))*(x(5)**j5)                    !hr04
!hr04               fder(l,1)=fder(l,1)+user(l*nmax+icont) *dble(j1)    &
!hr04&*(x(1)**(j1-1)) *(x(2)**j2)*(x(3)**j3) *(x(4)**j4)                &
!hr04&*(x(5)**j5)
