                fder(l,1)=fder(l,1)+((((((user(l*nmax+icont) *dble(j1)) &!hr04
     &*(x(1)**(j1-1)))*(x(2)**j2))*(x(3)**j3))*(x(4)**j4))              &!hr04
     &*(x(5)**j5))*(x(6)**j6)                                            !hr04
!hr04                 fder(l,2)=fder(l,2)+user(l*nmax+icont) *dble(j2)  &
