      subroutine readd2(user)
!-----------------------------------------------------------------------
!---- SUBROUTINE TO READ DATA
!-----------------------------------------------------------------------
      implicit none
+if cr
+ca crcoall
+ei
+if crlibm
+ca crlibco
+ei
      integer icont,ind,j,j1,j2,j3,j4,j5,j6,jcomp,jel,jord,jp,ncoef,njx,&
     &njx1,njz,njz1,nor,np,point,kointer
      real user
      double precision cc
+ca commadha
+ca commadh2
      dimension ind(10),user(500)
+if crlibm
      integer nchars
      parameter (nchars=160)
      character*(nchars) ch
      character*(nchars+nchars) ch1
      integer nofields
      parameter (nofields=20)
      character*(nchars) fields(nofields)
      integer errno,nfields,nunit,lineno,maxf,nf
      double precision fround
      data lineno /0/
+ei
+ca save
!-----------------------------------------------------------------------
      rewind 23
!-----------------------------------------------------------------------
! Unit 23 is opened round='nearest' if fio is selected
+if .not.crlibm
   10 read(23,*,end=40) ncoef,cc,nor,njx,njx1,njz,njz1,np,(ind(jel),jel &
     &=1,jeltot)
      read(23,*,end=40) cc
+ei
+if crlibm
   10 nunit=23
      read(23,*,end=40) ch
      lineno=lineno+1
      ch1(:nchars+3)=ch(:nchars)//' / '
      call splitfld(errno,23,lineno,nofields,nf,ch1,fields)
      if (nf.gt.0) then
        read (fields(1),*) ncoef
        nf=nf-1
      endif
      if (nf.gt.0) then
        cc=fround(errno,fields,2)
        nf=nf-1
      endif
      if (nf.gt.0) then
        read (fields(3),*) nor
        nf=nf-1
      endif
      if (nf.gt.0) then
        read (fields(4),*) njx
        nf=nf-1
      endif
      if (nf.gt.0) then
        read (fields(5),*) njx1
        nf=nf-1
      endif
      if (nf.gt.0) then
        read (fields(6),*) njz
        nf=nf-1
      endif
      if (nf.gt.0) then
        read (fields(7),*) njz1
        nf=nf-1
      endif
      if (nf.gt.0) then
        read (fields(8),*) np
        nf=nf-1
      endif
      do jel=1,jeltot
        if (nf.gt.0) then
          read (fields(8+jel),*) ind(jel)
          nf=nf-1
        endif
      enddo
      read(23,*,end=40) ch
      lineno=lineno+1
      ch1(:nchars+3)=ch(:nchars)//' / '
      call splitfld(errno,23,lineno,nofields,nf,ch1,fields)
      if (nf.gt.0) then
        cc=fround(errno,fields,1)
        nf=nf-1
      endif
+ei
!-----------------------------------------------------------------------
!---- CODING IND IN BASE NORDP+1
!-----------------------------------------------------------------------
      if(njx.eq.njx1.and.njz.eq.njz1.and.(njx+njz).eq.1.and. np.gt.0.and&
     &.np.le.nordp) then
        point=0
        do 20 j=1,jeltot
          point=point+ind(j)*(nordp+1)**(j-1)
   20   continue
        if(point.gt.8000) then
+if cr
          write(lout,10000)
+ei
+if .not.cr
          write(*,10000)
+ei
          call closeUnits
+if cr
      call abend('                                                  ')
+ei
+if .not.cr
          stop
+ei
        end if
!-----------------------------------------------------------------------
!---- DATA PROCESSING
!-----------------------------------------------------------------------
        hdp(njx,np,point)=cc+hdp(njx,np,point)
!-----------------------------------------------------------------------
      else if(njx.eq.njx1.and.njz.eq.njz1.and.(njx+njz).lt.10.and. np   &
     &.eq.0) then
        point=0
        norda=njx+njz
        do 30 j=1,jeltot
          point=point+ind(j)*(nordp+1)**(j-1)
   30   continue
        if(point.gt.8000) then
+if cr
          write(lout,10000)
+ei
+if .not.cr
          write(*,10000)
+ei
          call closeUnits
+if cr
      call abend('                                                  ')
+ei
+if .not.cr
          stop
+ei
        end if
!-----------------------------------------------------------------------
!---- DATA PROCESSING
!-----------------------------------------------------------------------
        hda(njx,njx+njz,point)=cc+hda(njx,njx+njz,point)
!-----------------------------------------------------------------------
      end if
!-----------------------------------------------------------------------
      goto 10
!-----------------------------------------------------------------------
!---- DEFINES DATA FOR THE ROUTINE OBJFUN
!-----------------------------------------------------------------------
   40 icont=5
!-----------------------------------------------------------------------
      if(jeltot.eq.1) then
!-----------------------------------------------------------------------
        do 70 jcomp=0,1
          do 60 jp=nordm,nordp
            do 50 jord=0,jp
              j1=jord
!-----------------------------------------------------------------------
              icont=icont+1
              kointer=j1
!-----------------------------------------------------------------------
              user(icont)=hdp(jcomp,jp,kointer)
   50       continue
   60     continue
   70   continue
!-----------------------------------------------------------------------
      else if(jeltot.eq.2) then
!-----------------------------------------------------------------------
        do 110 jcomp=0,1
          do 100 jp=nordm,nordp
            do 90 jord=0,jp
              do 80 j1=0,jord
                j2=jord-j1
!-----------------------------------------------------------------------
                icont=icont+1
                kointer=j1+j2*(nordp+1)
!-----------------------------------------------------------------------
                user(icont)=hdp(jcomp,jp,kointer)
   80         continue
   90       continue
  100     continue
  110   continue
!-----------------------------------------------------------------------
      else if(jeltot.eq.3) then
!-----------------------------------------------------------------------
        do 160 jcomp=0,1
          do 150 jp=nordm,nordp
            do 140 jord=0,jp
              do 130 j1=0,jord
                do 120 j2=0,jord-j1
                  j3=jord-j1-j2
!-----------------------------------------------------------------------
                  icont=icont+1
!hr04             kointer=j1+j2*(nordp+1)+j3*(nordp+1)*(nordp+1)
                  kointer=(j1+j2*(nordp+1))+j3*(nordp+1)**2              !hr04
!-----------------------------------------------------------------------
                  user(icont)=hdp(jcomp,jp,kointer)
  120           continue
  130         continue
  140       continue
  150     continue
  160   continue
!-----------------------------------------------------------------------
      else if(jeltot.eq.4) then
