        do 220 jcomp=0,1
          do 210 jp=nordm,nordp
            do 200 jord=0,jp
              do 190 j1=0,jord
                do 180 j2=0,jord-j1
                  do 170 j3=0,jord-j1-j2
                    j4=jord-j1-j2-j3
!-----------------------------------------------------------------------
                    icont=icont+1
!hr04               kointer=j1+j2*(nordp+1)+j3*(nordp+1)*(nordp+1 )+j4  &
!hr04&*(nordp+1)*(nordp+1)*(nordp+1)
                    kointer=((j1+j2*(nordp+1))+j3*(nordp+1)**2)+j4      &!hr04
     &*(nordp+1)**3                                                      !hr04
!-----------------------------------------------------------------------
                    user(icont)=hdp(jcomp,jp,kointer)
  170             continue
  180           continue
  190         continue
  200       continue
  210     continue
  220   continue
!-----------------------------------------------------------------------
      else if(jeltot.eq.5) then
!-----------------------------------------------------------------------
        do 290 jcomp=0,1
          do 280 jp=nordm,nordp
            do 270 jord=0,jp
              do 260 j1=0,jord
                do 250 j2=0,jord-j1
                  do 240 j3=0,jord-j1-j2
                    do 230 j4=0,jord-j1-j2-j3
                      j5=jord-j1-j2-j3-j4
!-----------------------------------------------------------------------
                      icont=icont+1
!hr04                 kointer=j1+j2*(nordp+1)+j3*(nordp+1)*( nordp+1)+j4&
!hr04&*(nordp+1)*(nordp+1 )*(nordp+1)+j5*(nordp+1)                      &
!hr04&*(nordp +1)*(nordp+1)*(nordp+1)
                      kointer=(((j1+j2*(nordp+1))+j3*(nordp+1)**2)+j4   &!hr04
     &*(nordp+1)**3)+j5*(nordp+1)**4                                     !hr04
!-----------------------------------------------------------------------
                      user(icont)=hdp(jcomp,jp,kointer)
  230               continue
  240             continue
  250           continue
  260         continue
  270       continue
  280     continue
  290   continue
!-----------------------------------------------------------------------
      else if(jeltot.eq.6) then
!-----------------------------------------------------------------------
        do 370 jcomp=0,1
          do 360 jp=nordm,nordp
            do 350 jord=0,jp
              do 340 j1=0,jord
                do 330 j2=0,jord-j1
                  do 320 j3=0,jord-j1-j2
                    do 310 j4=0,jord-j1-j2-j3
                      do 300 j5=0,jord-j1-j2-j3-j4
                        j6=jord-j1-j2-j3-j4-j6
!-----------------------------------------------------------------------
                        icont=icont+1
!hr04                   kointer=j1+j2*(nordp+1)+j3*(nordp+1)*( nordp+1) &
!hr04&+j4*(nordp+1)*(nordp+1 )*(nordp+1)+j5*(nordp+1)                   &
!hr04&*(nordp +1)*(nordp+1)*(nordp+1)+j6* (nordp+1)                     &
!hr04&*(nordp+1)*(nordp+1)* (nordp+1)*(nordp+1)
                        kointer=((((j1+j2*(nordp+1))+j3*(nordp+1)**2)   &!hr04
     &+j4*(nordp+1)**3)+j5*(nordp+1)**4)+j6*(nordp+1)**5                 !hr04
!-----------------------------------------------------------------------
                        user(icont)=hdp(jcomp,jp,kointer)
  300                 continue
  310               continue
  320             continue
  330           continue
  340         continue
  350       continue
  360     continue
  370   continue
!-----------------------------------------------------------------------
      end if
!-----------------------------------------------------------------------
!-----------------------------------------------------------------------
      return
10000 format(//,t10,' INDEX OUT OF BOUND IN ROUTINE READD ')
      end
      subroutine hamilton2(jp)
!-----------------------------------------------------------------------
!---- COMPUTES THE VALUE OF THE HAMILTONIAN AFTER CORRECTIONS
!-----------------------------------------------------------------------
      implicit none
+if cr
+ca crcoall
+ei
+if crlibm
+ca crlibco
+ei
      integer j,j1,j2,j3,j4,j5,j6,jel,jord,jp,l,kointer
      double precision thama,thamp
+ca commadha
+ca commadh2
      dimension thamp(0:1),thama(0:4)
+ca save
!-----------------------------------------------------------------------
      if(iamp.eq.0) then
!-----------------------------------------------------------------------
        thamp(0)=0d0
        thamp(1)=0d0
!-----------------------------------------------------------------------
        if(jeltot.eq.1) then
!-----------------------------------------------------------------------
          do 20 jord=0,jp
            j1=jord
!-----------------------------------------------------------------------
            kointer=j1
!-----------------------------------------------------------------------
            do 10 l=0,1
              thamp(l)=thamp(l)+hdp(l,jp,kointer)*(x(1)**j1)
   10       continue
   20     continue
!-----------------------------------------------------------------------
        else if(jeltot.eq.2) then
!-----------------------------------------------------------------------
          do 50 jord=0,jp
            do 40 j1=0,jord
              j2=jord-j1
!-----------------------------------------------------------------------
              kointer=j1+j2*(nordp+1)
!-----------------------------------------------------------------------
              do 30 l=0,1
                thamp(l)=thamp(l)+hdp(l,jp,kointer)*(x(1)**j1)          &
     &*(x(2)**j2)
   30         continue
   40       continue
   50     continue
!-----------------------------------------------------------------------
        else if(jeltot.eq.3) then
!-----------------------------------------------------------------------
          do 90 jord=0,jp
            do 80 j1=0,jord
              do 70 j2=0,jord-j1
                j3=jord-j1-j2
!-----------------------------------------------------------------------
!hr04           kointer=j1+j2*(nordp+1)+j3*(nordp+1)**2
                kointer=(j1+j2*(nordp+1))+j3*(nordp+1)**2                !hr04
!-----------------------------------------------------------------------
                do 60 l=0,1
!hr04             thamp(l)=thamp(l)+hdp(l,jp,kointer)*(x(1)**j1)        &
!hr04&*(x(2)**j2)*(x(3)**j3)
                  thamp(l)=thamp(l)+((hdp(l,jp,kointer)*(x(1)**j1))     &!hr04
     &*(x(2)**j2))*(x(3)**j3)                                            !hr04
   60           continue
   70         continue
   80       continue
   90     continue
!-----------------------------------------------------------------------
        else if(jeltot.eq.4) then
!-----------------------------------------------------------------------
          do 140 jord=0,jp
            do 130 j1=0,jord
              do 120 j2=0,jord-j1
                do 110 j3=0,jord-j1-j2
                  j4=jord-j1-j2-j3
!-----------------------------------------------------------------------
!hr04             kointer=j1+j2*(nordp+1)+j3*(nordp+1)**2 +j4*(nordp+1) &
!hr04&**3
               kointer=((j1+j2*(nordp+1))+j3*(nordp+1)**2)+j4*(nordp+1) &!hr04
     &**3                                                                !hr04
!-----------------------------------------------------------------------
                  do 100 l=0,1
!hr04               thamp(l)=thamp(l)+hdp(l,jp,kointer)*(x(1)**j1)      &
!hr04&*(x(2)**j2)*(x(3)**j3)*(x(4)**j4)
                    thamp(l)=thamp(l)+(((hdp(l,jp,kointer)*(x(1)**j1))  &!hr04
     &*(x(2)**j2))*(x(3)**j3))*(x(4)**j4)                                !hr04
  100             continue
  110           continue
  120         continue
  130       continue
  140     continue
!-----------------------------------------------------------------------
        else if(jeltot.eq.5) then
!-----------------------------------------------------------------------
          do 200 jord=0,jp
            do 190 j1=0,jord
              do 180 j2=0,jord-j1
                do 170 j3=0,jord-j1-j2
                  do 160 j4=0,jord-j1-j2-j3
                    j5=jord-j1-j2-j3-j4
!-----------------------------------------------------------------------
!hr04               kointer=j1+j2*(nordp+1)+j3*(nordp+1)**2 +j4         &
!hr04&*(nordp+1)**3+j5*(nordp+1)**4
                    kointer=(((j1+j2*(nordp+1))+j3*(nordp+1)**2)+j4     &!hr04
     &*(nordp+1)**3)+j5*(nordp+1)**4                                     !hr04
!-----------------------------------------------------------------------
                    do 150 l=0,1
!hr04                 thamp(l)=thamp(l)+hdp(l,jp,kointer) *(x(1)**j1)   &
!hr04&*(x(2)**j2)*(x(3)**j3) *(x(4)**j4)*(x(5)**j5)
                    thamp(l)=thamp(l)+((((hdp(l,jp,kointer)*(x(1)**j1)) &!hr04
     &*(x(2)**j2))*(x(3)**j3))*(x(4)**j4))*(x(5)**j5)                    !hr04
  150               continue
  160             continue
  170           continue
  180         continue
  190       continue
  200     continue
!-----------------------------------------------------------------------
        else if(jeltot.eq.6) then
!-----------------------------------------------------------------------
          do 270 jord=0,jp
            do 260 j1=0,jord
              do 250 j2=0,jord-j1
                do 240 j3=0,jord-j1-j2
                  do 230 j4=0,jord-j1-j2-j3
                    do 220 j5=0,jord-j1-j2-j3-j4
                      j6=jord-j1-j2-j3-j4-j5
!-----------------------------------------------------------------------
!hr04                 kointer=j1+j2*(nordp+1)+j3*(nordp+1)**2 +j4       &
!hr04&*(nordp+1)**3+j5*(nordp+1)**4 +j6*(nordp+1)**5
                      kointer=((((j1+j2*(nordp+1))+j3*(nordp+1)**2)+j4  &!hr04
     &*(nordp+1)**3)+j5*(nordp+1)**4)+j6*(nordp+1)**5                    !hr04
!-----------------------------------------------------------------------
                      do 210 l=0,1
!hr04                   thamp(l)=thamp(l)+hdp(l,jp,kointer) *(x(1)**j1) &
!hr04&*(x(2)**j2)*(x(3)**j3) *(x(4)**j4)*(x(5)**j5)                     &
!hr04&*(x(6)**j6)
                   thamp(l)=thamp(l)+(((((hdp(l,jp,kointer)*(x(1)**j1)) &
     &*(x(2)**j2))*(x(3)**j3))*(x(4)**j4))*(x(5)**j5))                  &
     &*(x(6)**j6)
  210                 continue
  220               continue
  230             continue
  240           continue
  250         continue
  260       continue
  270     continue
!-----------------------------------------------------------------------
        end if
!-----------------------------------------------------------------------
        do 280 jel=0,1
          hamp(jel)=thamp(jel)
  280   continue
!-----------------------------------------------------------------------
      else
!-----------------------------------------------------------------------
        do 290 j=0,4
          thama(j)=0.d0
  290   continue
!-----------------------------------------------------------------------
        if(jeltot.eq.1) then
!-----------------------------------------------------------------------
          do 310 jord=0,jp
            j1=jord
!-----------------------------------------------------------------------
            kointer=j1
!-----------------------------------------------------------------------
            do 300 l=0,jp
              thama(l)=thama(l)+hda(l,jp,kointer)*(x(1)**j1)
  300       continue
  310     continue
!-----------------------------------------------------------------------
        else if(jeltot.eq.2) then
!-----------------------------------------------------------------------
          do 340 jord=0,jp
            do 330 j1=0,jord
              j2=jord-j1
!-----------------------------------------------------------------------
              kointer=j1+j2*(nordp+1)
!-----------------------------------------------------------------------
              do 320 l=0,jp
                thama(l)=thama(l)+hda(l,jp,kointer)*(x(1)**j1)          &
     &*(x(2)**j2)
  320         continue
  330       continue
  340     continue
!-----------------------------------------------------------------------
        else if(jeltot.eq.3) then
!-----------------------------------------------------------------------
          do 380 jord=0,jp
            do 370 j1=0,jord
              do 360 j2=0,jord-j1
                j3=jord-j1-j2
!-----------------------------------------------------------------------
!hr04           kointer=j1+j2*(nordp+1)+j3*(nordp+1)**2
                kointer=(j1+j2*(nordp+1))+j3*(nordp+1)**2                !hr04
!-----------------------------------------------------------------------
                do 350 l=0,jp
!hr04             thama(l)=thama(l)+hda(l,jp,kointer)*(x(1)**j1)        &
!hr04&*(x(2)**j2)*(x(3)**j3)
                  thama(l)=thama(l)+((hda(l,jp,kointer)*(x(1)**j1))     &!hr04
     &*(x(2)**j2))*(x(3)**j3)                                            !hr04
  350           continue
  360         continue
  370       continue
  380     continue
!-----------------------------------------------------------------------
        else if(jeltot.eq.4) then
!-----------------------------------------------------------------------
          do 430 jord=0,jp
            do 420 j1=0,jord
              do 410 j2=0,jord-j1
                do 400 j3=0,jord-j1-j2
                  j4=jord-j1-j2-j3
!-----------------------------------------------------------------------
!hr04             kointer=j1+j2*(nordp+1)+j3*(nordp+1)**2 +j4*(nordp+1) &
!hr04&**3
               kointer=((j1+j2*(nordp+1))+j3*(nordp+1)**2)+j4*(nordp+1) &!hr04
     &**3                                                                !hr04
!-----------------------------------------------------------------------
                  do 390 l=0,jp
!hr04               thama(l)=thama(l)+hda(l,jp,kointer)*(x(1)**j1)      &
!hr04&*(x(2)**j2)*(x(3)**j3)*(x(4)**j4)
