+if .not.cr
                  write (*,*)   'error! too many fields in line:'
                  write (*,*)   tmpline
                  write (*,*)   'please increase getfields_n_max_fields'
+ei
                  getfields_lerr = .true.
                  exit !Break do
               endif
               istart = ii
               lchar = .true.
               lenstr = 0
            endif
            lenstr = lenstr+1
         endif
      enddo

