+ca commons
+ca commont1
+ca commondl
+if bnlelens
+ca rhicelens
+ei
+ca save
!-----------------------------------------------------------------------
      dpd=one+dpp
      dpsq=sqrt(dpd)
      do 190 i=1,il
        do ll=1,6
          do l=1,2
            al(ll,l,j,i)=zero
            as(ll,l,j,i)=zero
+if .not.vvector
            at(ll,l,j,i)=zero
            a2(ll,l,j,i)=zero
+ei
          enddo
        enddo
        if(abs(el(i)).le.pieni) goto 190
        kz1=kz(i)+1
        goto(10,30,90,50,70,80,120,170,180),kz1
        goto 190
!-----------------------------------------------------------------------
!  DRIFTLENGTH
!-----------------------------------------------------------------------
   10   do 20 l=1,2
          al(1,l,j,i)=one
          al(2,l,j,i)=el(i)
          al(3,l,j,i)=zero
          al(4,l,j,i)=one
!hr05   20   as(6,l,j,i)=-rv*al(2,l,j,i)/c2e3
   20   as(6,l,j,i)=((-1d0*rv)*al(2,l,j,i))/c2e3                         !hr05
!hr05   as(1,1,j,i)=el(i)*(one-rv)*c1e3
        as(1,1,j,i)=(el(i)*(one-rv))*c1e3                                !hr05
        goto 190
!-----------------------------------------------------------------------
!  RECTANGULAR MAGNET
!  HORIZONTAL
!-----------------------------------------------------------------------
   30   ih=1
!hr05   40   fok=el(i)*ed(i)/dpsq
   40   fok=(el(i)*ed(i))/dpsq                                           !hr05
        if(abs(fok).le.pieni) goto 10
        rho=(one/ed(i))*dpsq
+if crlibm
        fok1=(tan_rn(fok*half))/rho
+ei
+if .not.crlibm
        fok1=(tan(fok*half))/rho
+ei
+if crlibm
        si=sin_rn(fok)
+ei
+if .not.crlibm
        si=sin(fok)
+ei
+if crlibm
        co=cos_rn(fok)
+ei
+if .not.crlibm
        co=cos(fok)
+ei
        al(1,ih,j,i)=one
        al(2,ih,j,i)=rho*si
        al(3,ih,j,i)=zero
        al(4,ih,j,i)=one
!hr05   al(5,ih,j,i)=-dpp*(rho*(one-co)/dpsq)*c1e3
        al(5,ih,j,i)=((-1d0*dpp)*((rho*(one-co))/dpsq))*c1e3             !hr05
+if crlibm
!hr05   al(6,ih,j,i)=-dpp*(two*tan_rn(fok*half)/dpsq)*c1e3
        al(6,ih,j,i)=((-1d0*dpp)*((two*tan_rn(fok*half))/dpsq))*c1e3     !hr05
+ei
+if .not.crlibm
!hr05   al(6,ih,j,i)=-dpp*(two*tan(fok*half)/dpsq)*c1e3
        al(6,ih,j,i)=((-1d0*dpp)*((two*tan(fok*half))/dpsq))*c1e3        !hr05
+ei
+if crlibm
        sm1=cos_rn(fok)
+ei
+if .not.crlibm
        sm1=cos(fok)
+ei
+if crlibm
        sm2=sin_rn(fok)*rho
+ei
+if .not.crlibm
        sm2=sin(fok)*rho
+ei
+if crlibm
!hr05   sm3=-sin_rn(fok)/rho
        sm3=(-1d0*sin_rn(fok))/rho                                       !hr05
+ei
+if .not.crlibm
!hr05   sm3=-sin(fok)/rho
        sm3=(-1d0*sin(fok))/rho                                          !hr05
+ei
!hr05   sm5=-rho*dpsq*(one-sm1)
        sm5=((-1d0*rho)*dpsq)*(one-sm1)                                  !hr05
!hr05   sm6=-sm2*dpsq/rho
        sm6=((-1d0*sm2)*dpsq)/rho                                        !hr05
        sm12=el(i)-sm1*sm2
        sm23=sm2*sm3
!hr05   as3=-rv*(dpp*rho/(two*dpsq)*sm23+sm5)                
        as3=(-1d0*rv)*(((dpp*rho)/(two*dpsq))*sm23+sm5)                  !hr05
!hr05   as4=-rv*sm23/c2e3
        as4=((-1d0*rv)*sm23)/c2e3                                        !hr05
!hr05   as6=-rv*(el(i)+sm1*sm2)/c4e3
        as6=((-1d0*rv)*(el(i)+sm1*sm2))/c4e3                             !hr05
!hr05   as(1,ih,j,i)=(-rv*(dpp*dpp/(four*dpd)*sm12+dpp*(el(i)-sm2))+    &
!hr05&el(i)*(one-rv))*c1e3
        as(1,ih,j,i)=(el(i)*(one-rv)-rv*((dpp**2/(four*dpd))*sm12+      &!hr05
     &dpp*(el(i)-sm2)))*c1e3                                             !hr05
!hr05   as(2,ih,j,i)=-rv*(dpp/(two*rho*dpsq)*sm12+sm6)+fok1*as3
        as(2,ih,j,i)=fok1*as3-rv*((dpp/((two*rho)*dpsq))*sm12+sm6)       !hr05
        as(3,ih,j,i)=as3
!hr05   as(4,ih,j,i)=as4+two*as6*fok1
        as(4,ih,j,i)=as4+(two*as6)*fok1                                  !hr05
!hr05   as(5,ih,j,i)=-rv*sm12/(c4e3*rho*rho)+as6*fok1*fok1+fok1*as4
        as(5,ih,j,i)=(as6*fok1*2+fok1*as4)-(rv*sm12)/(c4e3*rho**2)       !hr05
        as(6,ih,j,i)=as6
!--VERTIKAL
        ih=ih+1
        if(ih.gt.2) ih=1
+if crlibm
        g=tan_rn(fok*half)/rho
+ei
+if .not.crlibm
        g=tan(fok*half)/rho
+ei
        gl=el(i)*g
        al(1,ih,j,i)=one-gl
        al(2,ih,j,i)=el(i)
!hr05   al(3,ih,j,i)=-g*(two-gl)
        al(3,ih,j,i)=(-1d0*g)*(two-gl)                                   !hr05
        al(4,ih,j,i)=al(1,ih,j,i)
!hr05   as6=-rv*al(2,ih,j,i)/c2e3
        as6=((-1d0*rv)*al(2,ih,j,i))/c2e3                                !hr05
!hr05   as(4,ih,j,i)=-two*as6*fok1                             
        as(4,ih,j,i)=((-1d0*two)*as6)*fok1                               !hr05
!hr05   as(5,ih,j,i)=as6*fok1*fok1
        as(5,ih,j,i)=as6*fok1**2                                         !hr05
        as(6,ih,j,i)=as6
        goto 190
!-----------------------------------------------------------------------
!  SEKTORMAGNET
!  HORIZONTAL
!-----------------------------------------------------------------------
   50   ih=1
!hr05   60   fok=el(i)*ed(i)/dpsq
   60   fok=(el(i)*ed(i))/dpsq                                           !hr05
        if(abs(fok).le.pieni) goto 10
        rho=(one/ed(i))*dpsq
+if crlibm
        si=sin_rn(fok)
+ei
+if .not.crlibm
        si=sin(fok)
+ei
+if crlibm
        co=cos_rn(fok)
+ei
+if .not.crlibm
        co=cos(fok)
+ei
!hr05   rhoc=rho*(one-co)/dpsq
        rhoc=(rho*(one-co))/dpsq                                         !hr05
        siq=si/dpsq
        al(1,ih,j,i)=co
        al(2,ih,j,i)=rho*si
!hr05   al(3,ih,j,i)=-si/rho
        al(3,ih,j,i)=(-1d0*si)/rho                                       !hr05
        al(4,ih,j,i)=co
!hr05   al(5,ih,j,i)=-dpp*rhoc*c1e3
        al(5,ih,j,i)=((-1d0*dpp)*rhoc)*c1e3                              !hr05
!hr05   al(6,ih,j,i)=-dpp*siq*c1e3
        al(6,ih,j,i)=((-1d0*dpp)*siq)*c1e3                               !hr05
        sm12=el(i)-al(1,ih,j,i)*al(2,ih,j,i)
        sm23=al(2,ih,j,i)*al(3,ih,j,i)
!hr05   as(1,ih,j,i)=(-rv*(dpp*dpp/(four*dpd)*sm12 +dpp*(el(i)-         &
!hr05&al(2,ih,j,i)))+el(i)*(one-rv))*c1e3
        as(1,ih,j,i)=(el(i)*(one-rv)-rv*((dpp**2/(four*dpd))*sm12 +     &!hr05
     &dpp*(el(i)-al(2,ih,j,i))))*c1e3                                    !hr05
!hr05   as(2,ih,j,i)=-rv*(dpp/(two*rho*dpsq)*sm12-dpd*siq)
        as(2,ih,j,i)=(-1d0*rv)*((dpp/((two*rho)*dpsq))*sm12-dpd*siq)     !hr05
!hr05   as(3,ih,j,i)=-rv*(dpp*rho/(two*dpsq)*sm23-dpd*rhoc)
        as(3,ih,j,i)=(-1d0*rv)*(((dpp*rho)/(two*dpsq))*sm23-dpd*rhoc)    !hr05
!hr05   as(4,ih,j,i)=-rv*sm23/c2e3                     
        as(4,ih,j,i)=((-1d0*rv)*sm23)/c2e3                               !hr05
!hr05   as(5,ih,j,i)=-rv*sm12/(c4e3*rho*rho)
        as(5,ih,j,i)=((-1d0*rv)*sm12)/(c4e3*rho**2)                      !hr05
!hr05   as(6,ih,j,i)=-rv*(el(i)+al(1,ih,j,i)*al(2,ih,j,i))/c4e3
        as(6,ih,j,i)=((-1d0*rv)*(el(i)+al(1,ih,j,i)*al(2,ih,j,i)))/c4e3  !hr05
!--VERTIKAL
        ih=ih+1
        if(ih.gt.2) ih=1
        al(1,ih,j,i)=one
        al(2,ih,j,i)=el(i)
        al(3,ih,j,i)=zero
        al(4,ih,j,i)=one
!hr05   as(6,ih,j,i)=-rv*al(2,ih,j,i)/c2e3
        as(6,ih,j,i)=((-1d0*rv)*al(2,ih,j,i))/c2e3                       !hr05
        goto 190
!-----------------------------------------------------------------------
!  RECTANGULAR MAGNET VERTIKAL
!-----------------------------------------------------------------------
   70   ih=2
        goto 40
!-----------------------------------------------------------------------
!  SEKTORMAGNET VERTIKAL
!-----------------------------------------------------------------------
   80   ih=2
        goto 60
!-----------------------------------------------------------------------
!  QUADRUPOLE
!  FOCUSSING
!-----------------------------------------------------------------------
   90   fok=ek(i)/(one+dpp)
        aek=abs(fok)
        if(abs(fok).le.pieni) goto 10
        ih=0
        hi=sqrt(aek)
        fi=el(i)*hi
        if(fok.gt.zero) goto 110
  100   ih=ih+1
+if crlibm
        al(1,ih,j,i)=cos_rn(fi)
+ei
+if .not.crlibm
        al(1,ih,j,i)=cos(fi)
+ei
+if crlibm
        hi1=sin_rn(fi)
+ei
+if .not.crlibm
        hi1=sin(fi)
+ei
        al(2,ih,j,i)=hi1/hi
!hr05   al(3,ih,j,i)=-hi1*hi
        al(3,ih,j,i)=(-1d0*hi1)*hi                                       !hr05
        al(4,ih,j,i)=al(1,ih,j,i)
!hr05   as(1,ih,j,i)=el(i)*(one-rv)*c1e3
        as(1,ih,j,i)=(el(i)*(one-rv))*c1e3                               !hr05
!hr05   as(4,ih,j,i)=-rv*al(2,ih,j,i)*al(3,ih,j,i)/c2e3
        as(4,ih,j,i)=(((-1d0*rv)*al(2,ih,j,i))*al(3,ih,j,i))/c2e3        !hr05
!hr05   as(5,ih,j,i)=-rv*(el(i)-al(1,ih,j,i)*al(2,ih,j,i))*aek/c4e3     
      as(5,ih,j,i)=(((-1d0*rv)*(el(i)-al(1,ih,j,i)*al(2,ih,j,i)))*aek)/ &!hr05
     &c4e3                                                               !hr05
!hr05   as(6,ih,j,i)=-rv*(el(i)+al(1,ih,j,i)*al(2,ih,j,i))/c4e3
        as(6,ih,j,i)=((-1d0*rv)*(el(i)+al(1,ih,j,i)*al(2,ih,j,i)))/c4e3  !hr05
        if(ih.eq.2) goto 190
!--DEFOCUSSING
  110   ih=ih+1
+if crlibm
        hp=exp_rn(fi)
+ei
+if .not.crlibm
        hp=exp(fi)
+ei
        hm=one/hp
        hc=(hp+hm)*half
        hs=(hp-hm)*half
        al(1,ih,j,i)=hc
        al(2,ih,j,i)=hs/hi
        al(3,ih,j,i)=hs*hi
        al(4,ih,j,i)=hc
!hr05   as(4,ih,j,i)=-rv*al(2,ih,j,i)*al(3,ih,j,i)/c2e3
        as(4,ih,j,i)=(((-1d0*rv)*al(2,ih,j,i))*al(3,ih,j,i))/c2e3        !hr05
!hr05   as(5,ih,j,i)=+rv*(el(i)-al(1,ih,j,i)*al(2,ih,j,i))*aek/c4e3
        as(5,ih,j,i)=((rv*(el(i)-al(1,ih,j,i)*al(2,ih,j,i)))*aek)/c4e3   !hr05
!hr05   as(6,ih,j,i)=-rv*(el(i)+al(1,ih,j,i)*al(2,ih,j,i))/c4e3
        as(6,ih,j,i)=((-1d0*rv)*(el(i)+al(1,ih,j,i)*al(2,ih,j,i)))/c4e3  !hr05
        if(ih.eq.1) goto 100
        goto 190
!-----------------------------------------------------------------------
!  COMBINED FUNCTION MAGNET HORIZONTAL
!  FOCUSSING
!-----------------------------------------------------------------------
  120   ih=0
        fokq=ek(i)
  130   wf=ed(i)/dpsq
!hr05   fok=fokq/(dpd)-wf*wf
        fok=fokq/(dpd)-wf**2                                             !hr05
        if(abs(fok).le.pieni) goto 10
        afok=abs(fok)
        hi=sqrt(afok)
        fi=hi*el(i)
        if(fok.gt.zero) goto 160
  140   ih=ih+1
+if crlibm
        si=sin_rn(fi)
+ei
+if .not.crlibm
        si=sin(fi)
+ei
+if crlibm
        co=cos_rn(fi)
+ei
+if .not.crlibm
        co=cos(fi)
+ei
!hr05   wfa=wf/afok*(one-co)/dpsq
        wfa=((wf/afok)*(one-co))/dpsq                                    !hr05
!hr05   wfhi=wf/hi*si/dpsq
        wfhi=((wf/hi)*si)/dpsq                                           !hr05
        al(1,ih,j,i)=co
        al(2,ih,j,i)=si/hi
!hr05   al(3,ih,j,i)=-si*hi
        al(3,ih,j,i)=(-1d0*si)*hi                                        !hr05
        al(4,ih,j,i)=co
!hr05   al(5,ih,j,i)=-wfa*dpp*c1e3
        al(5,ih,j,i)=((-1d0*wfa)*dpp)*c1e3                               !hr05
!hr05   al(6,ih,j,i)=-wfhi*dpp*c1e3
        al(6,ih,j,i)=((-1d0*wfhi)*dpp)*c1e3                              !hr05
        sm12=el(i)-al(1,ih,j,i)*al(2,ih,j,i)
        sm23=al(2,ih,j,i)*al(3,ih,j,i)
!hr05   as(1,ih,j,i)=(-rv*(dpp*dpp/(four*dpd)*sm12+dpp*(el(i)-          &
!hr05&al(2,ih,j,i)))/afok*wf*wf+el(i)*(one-rv))*c1e3
        as(1,ih,j,i)=(el(i)*(one-rv)-((rv*((dpp**2/(four*dpd))*sm12+    &!hr05
     &dpp*(el(i)-al(2,ih,j,i))))/afok)*wf**2)*c1e3                       !hr05
!hr05   as(2,ih,j,i)=-rv*(dpp*wf/(two*dpsq)*sm12-dpd*wfhi)
        as(2,ih,j,i)=(-1d0*rv)*(((dpp*wf)/(two*dpsq))*sm12-dpd*wfhi)     !hr05
!hr05   as(3,ih,j,i)=-rv*(dpp*half/afok/dpd*ed(i)*sm23-dpd*wfa)
        as(3,ih,j,i)=(-1d0*rv)*(((((dpp*half)/afok)/dpd)*ed(i))*sm23-   &!hr05
     &dpd*wfa)                                                           !hr05
!hr05   as(4,ih,j,i)=-rv*sm23/c2e3
        as(4,ih,j,i)=((-1d0*rv)*sm23)/c2e3                               !hr05
!hr05   as(5,ih,j,i)=-rv*sm12*afok/c4e3
        as(5,ih,j,i)=(((-1d0*rv)*sm12)*afok)/c4e3                        !hr05
!hr05   as(6,ih,j,i)=-rv*(el(i)+al(1,ih,j,i)*al(2,ih,j,i))/c4e3
        as(6,ih,j,i)=((-1d0*rv)*(el(i)+al(1,ih,j,i)*al(2,ih,j,i)))/c4e3  !hr05
        ih=ih+1
        if(ih.gt.2) ih=1
        aek=abs(ek(i)/dpd)
        hi=sqrt(aek)
        fi=hi*el(i)
+if crlibm
        hp=exp_rn(fi)
+ei
+if .not.crlibm
        hp=exp(fi)
+ei
        hm=one/hp
        hc=(hp+hm)*half
        hs=(hp-hm)*half
        al(1,ih,j,i)=hc
        al(2,ih,j,i)=el(i)
        if(abs(hi).le.pieni) goto 150
        al(2,ih,j,i)=hs/hi
  150   al(3,ih,j,i)=hs*hi
        al(4,ih,j,i)=hc
!hr05   as(4,ih,j,i)=-rv*al(2,ih,j,i)*al(3,ih,j,i)/c2e3
        as(4,ih,j,i)=(((-1d0*rv)*al(2,ih,j,i))*al(3,ih,j,i))/c2e3        !hr05
!hr05   as(5,ih,j,i)=+rv*(el(i)-al(1,ih,j,i)*al(2,ih,j,i))*aek/c4e3
        as(5,ih,j,i)=((rv*(el(i)-al(1,ih,j,i)*al(2,ih,j,i)))*aek)/c4e3   !hr05
!hr05   as(6,ih,j,i)=-rv*(el(i)+al(1,ih,j,i)*al(2,ih,j,i))/c4e3
        as(6,ih,j,i)=((-1d0*rv)*(el(i)+al(1,ih,j,i)*al(2,ih,j,i)))/c4e3  !hr05
        goto 190
!--DEFOCUSSING
  160   ih=ih+1
+if crlibm
        hp=exp_rn(fi)
+ei
+if .not.crlibm
        hp=exp(fi)
+ei
        hm=one/hp
        hc=(hp+hm)*half
        hs=(hp-hm)*half
        al(1,ih,j,i)=hc
        al(2,ih,j,i)=hs/hi
        al(3,ih,j,i)=hs*hi
        al(4,ih,j,i)=hc
!hr05   wfa=wf/afok*(one-hc)/dpsq
        wfa=((wf/afok)*(one-hc))/dpsq                                    !hr05
!hr05   wfhi=wf/hi*hs/dpsq
        wfhi=((wf/hi)*hs)/dpsq                                           !hr05
!hr05   al(5,ih,j,i)= wfa*dpp*c1e3
        al(5,ih,j,i)= (wfa*dpp)*c1e3                                     !hr05
!hr05   al(6,ih,j,i)=-wfhi*dpp*c1e3
        al(6,ih,j,i)=((-1d0*wfhi)*dpp)*c1e3                              !hr05
        sm12=el(i)-al(1,ih,j,i)*al(2,ih,j,i)
        sm23=al(2,ih,j,i)*al(3,ih,j,i)
!hr05   as(1,ih,j,i)=(rv*(dpp*dpp/(four*dpd)*sm12+dpp*(el(i)-           &
!hr05&al(2,ih,j,i)))/afok*wf*wf+el(i)*(one-rv))*c1e3
        as(1,ih,j,i)=(((rv*((dpp**2/(four*dpd))*sm12+dpp*(el(i)-        &!hr05
     &al(2,ih,j,i))))/afok)*wf**2+el(i)*(one-rv))*c1e3                   !hr05
!hr05   as(2,ih,j,i)=-rv*(dpp*wf/(two*dpsq)*sm12-dpd*wfhi)
        as(2,ih,j,i)=(-1d0*rv)*(((dpp*wf)/(two*dpsq))*sm12-dpd*wfhi)     !hr05
!hr05   as(3,ih,j,i)=rv*(dpp*half/afok/dpd*ed(i)*sm23-dpd*wfa)
        as(3,ih,j,i)=rv*(((((dpp*half)/afok)/dpd)*ed(i))*sm23-dpd*wfa)   !hr05
!hr05   as(4,ih,j,i)=-rv*sm23/c2e3
        as(4,ih,j,i)=((-1d0*rv)*sm23)/c2e3                               !hr05
!hr05   as(5,ih,j,i)=+rv*sm12*afok/c4e3
        as(5,ih,j,i)=((rv*sm12)*afok)/c4e3                               !hr05
!hr05   as(6,ih,j,i)=-rv*(el(i)+al(1,ih,j,i)*al(2,ih,j,i))/c4e3
        as(6,ih,j,i)=((-1d0*rv)*(el(i)+al(1,ih,j,i)*al(2,ih,j,i)))/c4e3  !hr05
        ih=ih+1
        if(ih.gt.2) ih=1
        aek=abs(ek(i)/dpd)
        hi=sqrt(aek)
        fi=hi*el(i)
+if crlibm
        si=sin_rn(fi)
+ei
+if .not.crlibm
        si=sin(fi)
+ei
+if crlibm
        co=cos_rn(fi)
+ei
+if .not.crlibm
        co=cos(fi)
+ei
        al(1,ih,j,i)=co
        al(2,ih,j,i)=si/hi
!hr05   al(3,ih,j,i)=-si*hi
        al(3,ih,j,i)=(-1d0*si)*hi                                        !hr05
        al(4,ih,j,i)=co
        as(4,ih,j,i)=(((-1d0*rv)*al(2,ih,j,i))*al(3,ih,j,i))/c2e3        !hr05
!hr05   as(5,ih,j,i)=-rv*(el(i)-al(1,ih,j,i)*al(2,ih,j,i))*aek/c4e3
      as(5,ih,j,i)=(((-1d0*rv)*(el(i)-al(1,ih,j,i)*al(2,ih,j,i)))*aek)/ &!hr05
     &c4e3                                                               !hr05
!hr05   as(6,ih,j,i)=-rv*(el(i)+al(1,ih,j,i)*al(2,ih,j,i))/c4e3
        as(6,ih,j,i)=((-1d0*rv)*(el(i)+al(1,ih,j,i)*al(2,ih,j,i)))/c4e3  !hr05
        goto 190
!-----------------------------------------------------------------------
!  COMBINED FUNCTION MAGNET VERTICAL
!-----------------------------------------------------------------------
  170   ih=1
!hr05   fokq=-ek(i)
        fokq=-1d0*ek(i)                                                  !hr05
        goto 130
!-----------------------------------------------------------------------
!  EDGE FOCUSSING
!-----------------------------------------------------------------------
  180   rhoi=ed(i)/dpsq
+if crlibm
!hr05   fok=rhoi*tan_rn(el(i)*rhoi*half)
        fok=rhoi*tan_rn((el(i)*rhoi)*half)                               !hr05
+ei
+if .not.crlibm
!hr05   fok=rhoi*tan(el(i)*rhoi*half)
        fok=rhoi*tan((el(i)*rhoi)*half)                                  !hr05
+ei
        al(1,1,j,i)=one
        al(2,1,j,i)=zero
        al(3,1,j,i)=fok
        al(4,1,j,i)=one
        al(1,2,j,i)=one
        al(2,2,j,i)=zero
!hr05   al(3,2,j,i)=-fok
        al(3,2,j,i)=-1d0*fok                                             !hr05
        al(4,2,j,i)=one
        goto 190
!-----------------------------------------------------------------------
!   NONLINEAR INSERTION
!-----------------------------------------------------------------------
  190 continue
+if .not.vvector
      do 220 k=1,mblo
        jm=mel(k)
        do 210 m=1,jm
          na=mtyp(k,m)
          ne=mtyp(k,jm-m+1)
          do 200 l=1,2
            at(1,l,j,na)=as(1,l,j,ne)
            at(2,l,j,na)=as(2,l,j,ne)
            at(3,l,j,na)=as(3,l,j,ne)
            at(4,l,j,na)=as(4,l,j,ne)
            at(5,l,j,na)=as(5,l,j,ne)
            at(6,l,j,na)=as(6,l,j,ne)
            a2(1,l,j,na)=al(1,l,j,ne)
            a2(2,l,j,na)=al(2,l,j,ne)
            a2(3,l,j,na)=al(3,l,j,ne)
            a2(4,l,j,na)=al(4,l,j,ne)
            a2(5,l,j,na)=al(5,l,j,ne)
            a2(6,l,j,na)=al(6,l,j,ne)
  200     continue
  210   continue
  220 continue
+ei
      return
      end
+dk envada
      subroutine envada
!-----------------------------------------------------------------------
!  CALCULATION OF : MOMENTUM-DEPENDING ELEMENT-MATRICES AND
!                   CHANGE OF PATH LENGTHS FOR EACH PARTICLE.
!      SPECIALLY PREPARED FOR NEW D.A. (SIX-DIMENSIONAL VERSION)
!-----------------------------------------------------------------------
      implicit none
+if cr
+ca crcoall
+ei
+if crlibm
+ca crlibco
+ei
      integer i,ien,ih,ip,kz1,l,idaa
      double precision dare,result
+ca parpro
+ca parnum
+ca common
+ca commons
+ca commont1
+ca commondl
+ca commond1
+ca commond2
+if bnlelens
+ca rhicelens
+ei
+ca save
!-----------------------------------------------------------------------
*FOX  B D ;
+ca dainicom
*FOX  D V DA INT FOKQ NORD NVAR ; D V DA INT WFHI NORD NVAR ;
*FOX  D V DA INT DPD NORD NVAR ; D V DA INT DPSQ NORD NVAR ;
*FOX  D V DA INT FOK NORD NVAR ; D V DA INT RHO NORD NVAR ;
*FOX  D V DA INT FOK1 NORD NVAR ; D V DA INT SM1 NORD NVAR ;
*FOX  D V DA INT SM2 NORD NVAR ; D V DA INT SM3 NORD NVAR ;
*FOX  D V DA INT SM4 NORD NVAR ; D V DA INT SM5 NORD NVAR ;
*FOX  D V DA INT SM6 NORD NVAR ; D V DA INT SM12 NORD NVAR ;
*FOX  D V DA INT SM23 NORD NVAR ; D V DA INT AS3 NORD NVAR ;
*FOX  D V DA INT AS4 NORD NVAR ; D V DA INT AS6 NORD NVAR ;
*FOX  D V DA INT SI NORD NVAR ; D V DA INT CO NORD NVAR ;
*FOX  D V DA INT G NORD NVAR ; D V DA INT GL NORD NVAR ;
*FOX  D V DA INT SIQ NORD NVAR ; D V DA INT RHOC NORD NVAR ;
*FOX  D V DA INT HI NORD NVAR ; D V DA INT FI NORD NVAR ;
*FOX  D V DA INT AEK NORD NVAR ; D V DA INT HI1 NORD NVAR ;
*FOX  D V DA INT HP NORD NVAR ; D V DA INT HM NORD NVAR ;
*FOX  D V DA INT HC NORD NVAR ; D V DA INT HS NORD NVAR ;
*FOX  D V DA INT FOKC NORD NVAR ; D V DA INT WF NORD NVAR ;
*FOX  D V DA INT AFOK NORD NVAR ; D V DA INT RHOI NORD NVAR ;
*FOX  D V DA INT WFA NORD NVAR ; D V RE INT RATIOE NELE ;
*FOX  D V RE INT EL NELE ; D V RE INT EK NELE ; D V RE INT ED NELE ;
*FOX  D V RE INT ONE ; D V RE INT ZERO ; D V RE INT TWO ;
*FOX  D V RE INT HALF ; D V RE INT FOUR ; D V RE INT C1E3 ;
*FOX  D V RE INT C2E3 ; D V RE INT C4E3 ;
*FOX  D V IN INT I ; D V IN INT L ; D V IN INT IH ; D V IN INT NE ;
*FOX  D V IN INT NA ; D V IN INT IP ; D V IN INT IPCH ;
*FOX  D F RE DARE 1 ;
*FOX  E D ;
*FOX  1 if(1.eq.1) then
!-----------------------------------------------------------------------
*FOX  DPD=ONE+DPDA ;
*FOX  DPSQ=SQRT(DPD) ;
      do 220 i=1,il
        do 10 ih=1,2
          do 10 ip=1,6
*FOX  ALDA(IH,IP)=ZERO ;
*FOX  ASDA(IH,IP)=ZERO ;
   10   continue
        if(abs(el(i)).le.pieni) goto 190
        kz1=kz(i)+1
!       goto(20,40,100,60,80,90,130,170,180),kz1
        if (kz1.eq.1) goto 20
        if (kz1.eq.2) goto 40
        if (kz1.eq.3) goto 100
        if (kz1.eq.4) goto 60
        if (kz1.eq.5) goto 80
        if (kz1.eq.6) goto 90
        if (kz1.eq.7) goto 130
        if (kz1.eq.8) goto 170
        if (kz1.eq.9) goto 180
        goto 220
!-----------------------------------------------------------------------
!  DRIFTLENGTH
!-----------------------------------------------------------------------
   20   do 30 l=1,2
*FOX  ALDA(L,1)=ONE  ;
*FOX  ALDA(L,2)=EL(I) ;
*FOX  ALDA(L,3)=ZERO ;
*FOX  ALDA(L,4)=ONE ;
*FOX  ASDA(L,6)=-RV*ALDA(L,2)/C2E3 ;
   30   continue
*FOX  ASDA(1,1)=EL(I)*(ONE-RV)*C1E3 ;
        goto 190
!-----------------------------------------------------------------------
!  RECTANGULAR MAGNET
!  HORIZONTAL
!-----------------------------------------------------------------------
   40   ih=1
   50   continue
        if(abs(ed(i)).le.pieni) goto 20
*FOX  FOK=EL(I)*ED(I)/DPSQ ;
*FOX  RHO=ONE/ED(I)*DPSQ ;
*FOX  FOK1=SIN(FOK*HALF)/COS(FOK*HALF)/RHO ;
*FOX  SI=SIN(FOK) ;
*FOX  CO=COS(FOK) ;
*FOX  ALDA(IH,1)=ONE ;
*FOX  ALDA(IH,2)=RHO*SI ;
*FOX  ALDA(IH,3)=ZERO ;
*FOX  ALDA(IH,4)=ONE ;
*FOX  ALDA(IH,5)=-DPDA*RHO*(ONE-CO)/DPSQ*C1E3 ;
*FOX  ALDA(IH,6)=-DPDA*TWO*SIN(FOK*HALF)/COS(FOK*HALF)/DPSQ*C1E3 ;
*FOX  SM1=COS(FOK) ;
*FOX  SM2=SIN(FOK)*RHO ;
*FOX  SM3=-SIN(FOK)/RHO ;
*FOX  SM5=-RHO*DPSQ*(ONE-SM1) ;
*FOX  SM6=-SM2*DPSQ/RHO ;
*FOX  SM12=EL(I)-SM1*SM2 ;
*FOX  SM23=SM2*SM3 ;
*FOX  AS3=-RV*(DPDA*RHO/(TWO*DPSQ)*SM23+SM5) ;
*FOX  AS4=-RV*SM23/C2E3 ;
*FOX  AS6=-RV*(EL(I)+SM1*SM2)/C4E3 ;
*FOX  ASDA(IH,1)=(-RV*(DPDA*DPDA/(FOUR*DPD)*SM12+DPDA*(EL(I)-SM2))
*FOX  +EL(I)*(ONE-RV))*C1E3 ;
*FOX  ASDA(IH,2)=-RV*(DPDA/(TWO*RHO*DPSQ)*SM12+SM6)+FOK1*AS3 ;
*FOX  ASDA(IH,3)=AS3 ;
*FOX  ASDA(IH,4)=AS4+TWO*AS6*FOK1 ;
*FOX  ASDA(IH,5)=-RV*SM12/(C4E3*RHO*RHO)+AS6*FOK1*FOK1+FOK1*AS4  ;
*FOX  ASDA(IH,6)=AS6 ;
!--VERTIKAL
        ih=ih+1
        if(ih.gt.2) ih=1
*FOX  G=SIN(FOK*HALF)/COS(FOK*HALF)/RHO ;
*FOX  GL=EL(I)*G ;
*FOX  ALDA(IH,1)=ONE-GL ;
*FOX  ALDA(IH,2)=EL(I) ;
*FOX  ALDA(IH,3)=-G*(TWO-GL) ;
*FOX  ALDA(IH,4)=ALDA(IH,1) ;
*FOX  AS6=-RV*ALDA(IH,2)/C2E3 ;
*FOX  ASDA(IH,4)=-TWO*AS6*FOK1 ;
*FOX  ASDA(IH,5)=AS6*FOK1*FOK1 ;
*FOX  ASDA(IH,6)=AS6 ;
        goto 190
!-----------------------------------------------------------------------
!  SEKTORMAGNET
!  HORIZONTAL
!-----------------------------------------------------------------------
   60   ih=1
   70   continue
        if(abs(ed(i)).le.pieni) goto 20
*FOX  FOK=EL(I)*ED(I)/DPSQ ;
*FOX  RHO=(ONE/ED(I))*DPSQ ;
*FOX  SI=SIN(FOK) ;
*FOX  CO=COS(FOK) ;
*FOX  RHOC=RHO*(ONE-CO)/DPSQ ;
*FOX  SIQ=SI/DPSQ ;
*FOX  ALDA(IH,1)=CO ;
*FOX  ALDA(IH,2)=RHO*SI ;
*FOX  ALDA(IH,3)=-SI/RHO ;
*FOX  ALDA(IH,4)=CO ;
*FOX  ALDA(IH,5)=-DPDA*RHOC*C1E3 ;
*FOX  ALDA(IH,6)=-DPDA*SIQ*C1E3 ;
*FOX  SM12=EL(I)-ALDA(IH,1)*ALDA(IH,2) ;
*FOX  SM23=ALDA(IH,2)*ALDA(IH,3) ;
*FOX  ASDA(IH,1)=(-RV*(DPDA*DPDA/(FOUR*DPD)*SM12
*FOX  +DPDA*(EL(I)-ALDA(IH,2)))+EL(I)*(ONE-RV))*C1E3 ;
*FOX  ASDA(IH,2)=-RV*(DPDA/(TWO*RHO*DPSQ)*SM12-DPD*SIQ) ;
*FOX  ASDA(IH,3)=-RV*(DPDA*RHO/(TWO*DPSQ)*SM23-DPD*RHOC) ;
*FOX  ASDA(IH,4)=-RV*SM23/C2E3 ;
*FOX  ASDA(IH,5)=-RV*SM12/(C4E3*RHO*RHO) ;
*FOX  ASDA(IH,6)=-RV*(EL(I)+ALDA(IH,1)*ALDA(IH,2))/C4E3 ;
!--VERTIKAL
        ih=ih+1
        if(ih.gt.2) ih=1
*FOX  ALDA(IH,1)=ONE ;
*FOX  ALDA(IH,2)=EL(I) ;
*FOX  ALDA(IH,3)=ZERO ;
*FOX  ALDA(IH,4)=ONE ;
*FOX  ASDA(IH,6)=-RV*ALDA(IH,2)/C2E3 ;
        goto 190
!-----------------------------------------------------------------------
!  RECTANGULAR MAGNET VERTIKAL
!-----------------------------------------------------------------------
   80   ih=2
        goto 50
!-----------------------------------------------------------------------
!  SEKTORMAGNET VERTIKAL
!-----------------------------------------------------------------------
   90   ih=2
        goto 70
!-----------------------------------------------------------------------
!  QUADRUPOLE
!  FOCUSSING
!-----------------------------------------------------------------------
  100   continue
        if(abs(ek(i)).le.pieni) goto 20
*FOX  FOK=EK(I)/(ONE+DPDA) ;
*FOX  AEK=FOK ;
        if(dare(aek).lt.zero) then
*FOX  AEK=-AEK ;
        endif
        ih=0
*FOX  HI=SQRT(AEK) ;
*FOX  FI=EL(I)*HI ;
        if(ek(i).gt.zero) goto 120
  110   ih=ih+1
*FOX  ALDA(IH,1)=COS(FI) ;
*FOX  HI1=SIN(FI) ;
*FOX  ALDA(IH,2)=HI1/HI ;
*FOX  ALDA(IH,3)=-HI1*HI ;
*FOX  ALDA(IH,4)=ALDA(IH,1) ;
*FOX  ASDA(IH,1)=EL(I)*(ONE-RV)*C1E3 ;
*FOX  ASDA(IH,4)=-RV*ALDA(IH,2)*ALDA(IH,3)/C2E3 ;
*FOX  ASDA(IH,5)=-RV*(EL(I)-ALDA(IH,1)*ALDA(IH,2))*AEK/C4E3 ;
*FOX  ASDA(IH,6)=-RV*(EL(I)+ALDA(IH,1)*ALDA(IH,2))/C4E3 ;
        if(ih.eq.2) goto 190
!--DEFOCUSSING
  120   ih=ih+1
*FOX  HP=EXP(FI) ;
*FOX  HM=ONE/HP ;
*FOX  HC=(HP+HM)*HALF ;
*FOX  HS=(HP-HM)*HALF ;
*FOX  ALDA(IH,1)=HC ;
*FOX  ALDA(IH,2)=HS/HI ;
*FOX  ALDA(IH,3)=HS*HI ;
*FOX  ALDA(IH,4)=HC ;
*FOX  ASDA(IH,4)=-RV*ALDA(IH,2)*ALDA(IH,3)/C2E3 ;
*FOX  ASDA(IH,5)=+RV*(EL(I)-ALDA(IH,1)*ALDA(IH,2))*AEK/C4E3 ;
*FOX  ASDA(IH,6)=-RV*(EL(I)+ALDA(IH,1)*ALDA(IH,2))/C4E3 ;
        if(ih.eq.1) goto 110
        goto 190
!-----------------------------------------------------------------------
!  COMBINED FUNCTION MAGNET HORIZONTAL
!  FOCUSSING
!-----------------------------------------------------------------------
  130   ih=0
*FOX  FOKQ=EK(I) ;
  140   continue
        if(abs(ek(i)).le.pieni) goto 60
        if(abs(ed(i)).le.pieni) goto 100
!hr08   if(abs(ek(i)-ed(i)*ed(i)).le.pieni) goto 20
        if(abs(ek(i)-ed(i)**2).le.pieni) goto 20                         !hr08
*FOX  WF=ED(I)/DPSQ ;
*FOX  FOK=FOKQ/DPD-WF*WF ;
*FOX  AFOK=FOK ;
      if(dare(afok).lt.zero) then
*FOX  AFOK=-AFOK ;
      endif
*FOX  HI=SQRT(AFOK) ;
*FOX  FI=HI*EL(I) ;
        if(dare(fok).gt.zero) goto 160
  150   ih=ih+1
*FOX  SI=SIN(FI) ;
*FOX  CO=COS(FI) ;
*FOX  WFA=WF/AFOK*(ONE-CO)/DPSQ ;
*FOX  WFHI=WF/HI*SI/DPSQ ;
*FOX  ALDA(IH,1)=CO ;
*FOX  ALDA(IH,2)=SI/HI ;
*FOX  ALDA(IH,3)=-SI*HI ;
*FOX  ALDA(IH,4)=CO ;
*FOX  ALDA(IH,5)=-WFA*DPDA*C1E3 ;
*FOX  ALDA(IH,6)=-WFHI*DPDA*C1E3 ;
*FOX  SM12=EL(I)-ALDA(IH,1)*ALDA(IH,2) ;
*FOX  SM23=ALDA(IH,2)*ALDA(IH,3) ;
*FOX  ASDA(IH,1)=(-RV*(DPDA*DPDA/(FOUR*DPD)*SM12+DPDA
*FOX  *(EL(I)-ALDA(IH,2)))/AFOK*WF*WF+EL(I)*(ONE-RV))*C1E3 ;
*FOX  ASDA(IH,2)=-RV*(DPDA*WF/(TWO*DPSQ)*SM12-DPD*WFHI) ;
*FOX  ASDA(IH,3)=-RV*(DPDA*HALF/AFOK/DPD*ED(I)*SM23-DPD*WFA) ;
*FOX  ASDA(IH,4)=-RV*SM23/C2E3 ;
*FOX  ASDA(IH,5)=-RV*SM12*AFOK/C4E3 ;
*FOX  ASDA(IH,6)=-RV*(EL(I)+ALDA(IH,1)*ALDA(IH,2))/C4E3 ;
        ih=ih+1
        if(ih.gt.2) ih=1
*FOX  AEK=EK(I)/DPD ;
      if(dare(aek).lt.zero) then
*FOX  AEK=-AEK ;
      endif
*FOX  HI=SQRT(AEK) ;
*FOX  FI=HI*EL(I) ;
*FOX  HP=EXP(FI) ;
*FOX  HM=ONE/HP ;
*FOX  HC=(HP+HM)*HALF ;
*FOX  HS=(HP-HM)*HALF ;
*FOX  ALDA(IH,1)=HC ;
*FOX  ALDA(IH,2)=EL(I) ;
*FOX  ALDA(IH,2)=HS/HI ;
*FOX  ALDA(IH,3)=HS*HI ;
*FOX  ALDA(IH,4)=HC ;
*FOX  ASDA(IH,4)=-RV*ALDA(IH,2)*ALDA(IH,3)/C2E3 ;
*FOX  ASDA(IH,5)=+RV*(EL(I)-ALDA(IH,1)*ALDA(IH,2))*AEK/C4E3 ;
*FOX  ASDA(IH,6)=-RV*(EL(I)+ALDA(IH,1)*ALDA(IH,2))/C4E3 ;
        goto 190
!--DEFOCUSSING
  160   ih=ih+1
*FOX  HP=EXP(FI) ;
*FOX  HM=ONE/HP ;
*FOX  HC=(HP+HM)*HALF ;
*FOX  HS=(HP-HM)*HALF ;
*FOX  ALDA(IH,1)=HC ;
*FOX  ALDA(IH,2)=HS/HI ;
*FOX  ALDA(IH,3)=HS*HI ;
*FOX  ALDA(IH,4)=HC ;
*FOX  WFA=WF/AFOK*(ONE-HC)/DPSQ ;
*FOX  WFHI=WF/HI*HS/DPSQ ;
*FOX  ALDA(IH,5)= WFA*DPDA*C1E3 ;
*FOX  ALDA(IH,6)=-WFHI*DPDA*C1E3 ;
*FOX  SM12=EL(I)-ALDA(IH,1)*ALDA(IH,2) ;
*FOX  SM23=ALDA(IH,2)*ALDA(IH,3) ;
*FOX  ASDA(IH,1)=(RV*(DPDA*DPDA/(FOUR*DPD)*SM12
*FOX  +DPDA*(EL(I)-ALDA(IH,2)))/AFOK*WF*WF+EL(I)*(ONE-RV))*C1E3 ;
*FOX  ASDA(IH,2)=-RV*(DPDA*WF/(TWO*DPSQ)*SM12-DPD*WFHI) ;
*FOX  ASDA(IH,3)=RV*(DPDA*HALF/AFOK/DPD*ED(I)*SM23-DPD*WFA) ;
*FOX  ASDA(IH,4)=-RV*SM23/C2E3 ;
*FOX  ASDA(IH,5)=+RV*SM12*AFOK/C4E3 ;
*FOX  ASDA(IH,6)=-RV*(EL(I)+ALDA(IH,1)*ALDA(IH,2))/C4E3 ;
        ih=ih+1
        if(ih.gt.2) ih=1
*FOX  AEK=EK(I)/DPD ;
      if(dare(aek).lt.zero) then
*FOX  AEK=-AEK ;
      endif
*FOX  HI=SQRT(AEK) ;
*FOX  FI=HI*EL(I) ;
*FOX  SI=SIN(FI) ;
*FOX  CO=COS(FI) ;
*FOX  ALDA(IH,1)=CO ;
*FOX  ALDA(IH,2)=SI/HI ;
*FOX  ALDA(IH,3)=-SI*HI ;
*FOX  ALDA(IH,4)=CO ;
*FOX  ASDA(IH,4)=-RV*ALDA(IH,2)*ALDA(IH,3)/C2E3 ;
*FOX  ASDA(IH,5)=-RV*(EL(I)-ALDA(IH,1)*ALDA(IH,2))*AEK/C4E3 ;
*FOX  ASDA(IH,6)=-RV*(EL(I)+ALDA(IH,1)*ALDA(IH,2))/C4E3 ;
        goto 190
!-----------------------------------------------------------------------
!  COMBINED FUNCTION MAGNET VERTICAL
!-----------------------------------------------------------------------
  170   ih=1
*FOX  FOKQ=-EK(I) ;
        goto 140
!-----------------------------------------------------------------------
!  EDGE FOCUSSING
!-----------------------------------------------------------------------
  180   continue
*FOX  RHOI=ED(I)/DPSQ ;
*FOX  FOK=RHOI*SIN(EL(I)*RHOI*HALF)/COS(EL(I)*RHOI*HALF) ;
*FOX  ALDA(1,1)=ONE ;
*FOX  ALDA(1,2)=ZERO ;
*FOX  ALDA(1,3)=FOK ;
*FOX  ALDA(1,4)=ONE ;
*FOX  ALDA(2,1)=ONE ;
*FOX  ALDA(2,2)=ZERO ;
*FOX  ALDA(2,3)=-FOK ;
*FOX  ALDA(2,4)=ONE ;
        goto 190
!-----------------------------------------------------------------------
!   NONLINEAR INSERTION
!-----------------------------------------------------------------------
  190   continue
        do 210 ih=1,2
          do 210 ip=1,6
            do 200 ien=1,nord+1
              if (nvar2.eq.5) then
                call dapri6(alda(ih,ip),result,ien,5)
                ald6(i,ih,ip,ien) = result
                call dapri6(asda(ih,ip),result,ien,5)
                asd6(i,ih,ip,ien) = result
              else if (nvar2.eq.6) then
                call dapri6(alda(ih,ip),result,ien,6)
                ald6(i,ih,ip,ien) = result
                call dapri6(asda(ih,ip),result,ien,6)
                asd6(i,ih,ip,ien) = result
              else if (nvar2.eq.4) then
                call dapri6(alda(ih,ip),result,ien,4)
                ald6(i,ih,ip,ien) = result
                call dapri6(asda(ih,ip),result,ien,4)
                asd6(i,ih,ip,ien) = result
              endif
  200       continue
  210   continue
  220 continue
!     DADAL AUTOMATIC INCLUSION
      return
      end
      subroutine envquad(i,ipch)
!-----------------------------------------------------------------------
!  CALCULATION OF : MOMENTUM-DEPENDING ELEMENT-MATRICES AND
!                   CHANGE OF PATH LENGTHS FOR EACH PARTICLE.
!      SPECIALLY PREPARED FOR NEW D.A. (SIX-DIMENSIONAL VERSION)
!-----------------------------------------------------------------------
      implicit none
+if cr
+ca crcoall
+ei
+if crlibm
+ca crlibco
+ei
      integer i,ih,ipch,idaa
      double precision dare
+ca parpro
+ca parnum
+ca common
+ca commondl
+ca commond1
+if bnlelens
+ca rhicelens
+ei
+ca save
!-----------------------------------------------------------------------
*FOX  B D ;
+ca dainicom
*FOX  D V DA INT FOKQ NORD NVAR ; D V DA INT WFHI NORD NVAR ;
*FOX  D V DA INT DPD NORD NVAR ; D V DA INT DPSQ NORD NVAR ;
*FOX  D V DA INT FOK NORD NVAR ; D V DA INT RHO NORD NVAR ;
*FOX  D V DA INT FOK1 NORD NVAR ; D V DA INT SM1 NORD NVAR ;
*FOX  D V DA INT SM2 NORD NVAR ; D V DA INT SM3 NORD NVAR ;
*FOX  D V DA INT SM4 NORD NVAR ; D V DA INT SM5 NORD NVAR ;
*FOX  D V DA INT SM6 NORD NVAR ; D V DA INT SM12 NORD NVAR ;
*FOX  D V DA INT SM23 NORD NVAR ; D V DA INT AS3 NORD NVAR ;
*FOX  D V DA INT AS4 NORD NVAR ; D V DA INT AS6 NORD NVAR ;
*FOX  D V DA INT SI NORD NVAR ; D V DA INT CO NORD NVAR ;
*FOX  D V DA INT G NORD NVAR ; D V DA INT GL NORD NVAR ;
*FOX  D V DA INT SIQ NORD NVAR ; D V DA INT RHOC NORD NVAR ;
*FOX  D V DA INT HI NORD NVAR ; D V DA INT FI NORD NVAR ;
*FOX  D V DA INT AEK NORD NVAR ; D V DA INT HI1 NORD NVAR ;
*FOX  D V DA INT HP NORD NVAR ; D V DA INT HM NORD NVAR ;
*FOX  D V DA INT HC NORD NVAR ; D V DA INT HS NORD NVAR ;
*FOX  D V DA INT FOKC NORD NVAR ; D V DA INT WF NORD NVAR ;
*FOX  D V DA INT AFOK NORD NVAR ; D V DA INT RHOI NORD NVAR ;
*FOX  D V DA INT WFA NORD NVAR ; D V RE INT RATIOE NELE ;
*FOX  D V RE INT EL NELE ; D V RE INT EK NELE ; D V RE INT ED NELE ;
*FOX  D V RE INT ONE ; D V RE INT ZERO ; D V RE INT TWO ;
*FOX  D V RE INT HALF ; D V RE INT FOUR ; D V RE INT C1E3 ;
*FOX  D V RE INT C2E3 ; D V RE INT C4E3 ;
*FOX  D V IN INT I ; D V IN INT L ; D V IN INT IH ; D V IN INT NE ;
*FOX  D V IN INT NA ; D V IN INT IP ; D V IN INT IPCH ;
*FOX  D F RE DARE 1 ;
*FOX  E D ;
*FOX  1 if(1.eq.1) then
!-----------------------------------------------------------------------
*FOX  DPD=ONE+DPDA ;
*FOX  DPSQ=SQRT(DPD) ;
!-----------------------------------------------------------------------
!  QUADRUPOLE
!  FOCUSSING
!-----------------------------------------------------------------------
      if(abs(ek(i)).le.pieni) goto 100
*FOX  FOK=(SMIDA(IPCH)*RATIOE(I))/(ONE+DPDA) ;
*FOX  AEK=FOK ;
      if(dare(aek).lt.zero) then
*FOX  AEK=-AEK ;
      endif
      ih=0
