      integer, parameter :: samplenumber = 1
+ei


!     temporary variables
      integer j
      character*16 localBez

+if cr      
      !For accessing dumpfilepos
      integer dumpIdx
      if( unit .eq. dumpunit(0) ) then
         ! ALL output must be on separate unit
         dumpIdx = 0
      else
         dumpIdx = ix
      endif
+ei
      if ( ktrack(i) .ne. 1 ) then
         localBez = bez(ix)
      else
