      implicit none
+if cr
+ca crcoall
+ei
+if crlibm
+ca crlibco
+ei
      integer ih1,ih2,j,kz1,l
      double precision fokm
+ca parpro
+ca parnum
+ca common
+ca commons
+ca commont1
+ca commondl
+ca commonxz
+ca commonta
+ca commonmn
+ca commonm1
+ca commontr
+if bnlelens
+ca rhicelens
