         as3(j)=(-1d0*rvv(j))*(((dpsv(j)*rho(j))/(two*dpsq(j)))*sm23(j)-&!hr06
     &(rho(j)*dpsq(j))*(one-sm1(j)))                                     !hr06
!hr06     as4(j)=-rvv(j)*sm23(j)/c2e3
          as4(j)=((-1d0*rvv(j))*sm23(j))/c2e3                            !hr06
