+dk clorb2
      subroutine clorb2(dpp)
!-----------------------------------------------------------------------
!  CALCULATION OF THE CLOSED ORBIT - NO WRITEOUT
!-----------------------------------------------------------------------
      implicit none
+if cr
+ca crcoall
+ei
+if crlibm
+ca crlibco
+ei
      integer ierr,ii,l,ll
      double precision am,dclo,dclop,dcx,dcxp,dcz,dczp,det,dpp,dx,dy,x0,&
     &x1,y0,y1
+ca parpro
+ca parnum
+ca common
+ca commons
+ca commont1
      dimension x1(2),y1(2),x0(2),y0(2)
      dimension dclo(2),dclop(2)
      dimension dx(2),dy(2),am(4,4)
+if bnlelens
+ca rhicelens
+ei
+ca save
!-----------------------------------------------------------------------
      ierro=0
      do 10 l=1,2
        clo(l)=dpp*di0(l)
        clop(l)=dpp*dip0(l)
!hr06   dx(l)=1e6
        dx(l)=1d6                                                        !hr06
!hr06   dy(l)=1e6
        dy(l)=1d6                                                        !hr06
   10 continue
      call envar(dpp)
      call umlauf(dpp,1,ierr)
      ierro=ierr
      if(ierro.ne.0) call prror(36)
      do 40 ii=1,itco
        dcx=abs(dx(1))
        dcxp=abs(dy(1))
        dcz=abs(dx(2))
        dczp=abs(dy(2))
        if(dcx.le.dma.and.dcz.le.dma.and.dcxp.le.dmap.and.dczp.le.dmap) &
     &return
        do 20 l=1,2
          x(1,l)=clo(l)
          y(1,l)=clop(l)
          x0(l)=x(1,l)
   20   y0(l)=y(1,l)
        call matrix(dpp,am)
        if(ierro.ne.0) call prror(36)
        do 30 l=1,2
          ll=2*l
          x1(l)=x(1,l)
          y1(l)=y(1,l)
          det=two-am(ll-1,ll-1)-am(ll,ll)
          dx(l)=x0(l)-x1(l)
          dy(l)=y0(l)-y1(l)
          dclo(l)=(dx(l)*(am(ll,ll)-one)-dy(l)*am(ll-1,ll))/det
          dclop(l)=(dy(l)*(am(ll-1,ll-1)-one)-dx(l)*am(ll,ll-1))/det
          clo(l)=clo(l)+dclo(l)
          clop(l)=clop(l)+dclop(l)
   30   continue
   40 continue
!-----------------------------------------------------------------------
      return
      end
+dk combel
      subroutine combel(iql)
!-----------------------------------------------------------------------
!  COMBINATION OF ELEMENTS
!-----------------------------------------------------------------------
      implicit none
+if cr
+ca crcoall
+ei
+if crlibm
+ca crlibco
+ei
      integer ico,ico0,iql,j,m
+ca parpro
+ca parnum
+ca common
+ca commons
+ca commont1
+if bnlelens
+ca rhicelens
+ei
+ca save
!-----------------------------------------------------------------------
      do 20 j=1,icoe
        ico0=icomb0(j)
        if(iql.ne.ico0) goto 20
        do 10 m=1,20
          ico=icomb(j,m)
          if(ico.eq.0) goto 10
          if(kz(ico0).ne.kz(ico)) call prror(14)
          if(abs(el(ico0)).gt.pieni) then
            if(abs(el(ico)).gt.pieni) then
              ek(ico)=ek(ico0)*ratio(j,m)
            else
              ed(ico)=ek(ico0)*ratio(j,m)
            endif
          endif
          if(abs(el(ico0)).le.pieni) then
            if(abs(el(ico)).le.pieni) then
              ed(ico)=ed(ico0)*ratio(j,m)
            else
              ek(ico)=ed(ico0)*ratio(j,m)
            endif
          endif
   10   continue
   20 continue
!-----------------------------------------------------------------------
      return
      end
+dk envar
      subroutine envar(dpp)
!-----------------------------------------------------------------------
!  CALCULATION OF ELEMENT MATRICES
!-----------------------------------------------------------------------
      implicit none
+if cr
+ca crcoall
+ei
+if crlibm
+ca crlibco
+ei
      integer i,ih,kz1,l,ll
      double precision afok,co,dpd,dpp,dpsq,fi,fok,fokq,g,gl,hc,hi,hi1, &
     &hm,hp,hs,rho,rhoi,si,wf
+ca parpro
+ca parnum
+ca common
+ca commons
+ca commont1
+if bnlelens
+ca rhicelens
+ei
+ca save
!-----------------------------------------------------------------------
      dpd=one+dpp
      dpsq=sqrt(dpd)
      do 200 i=1,il
        do ll=1,6
          do l=1,2
            a(i,l,ll)=zero
          enddo
        enddo
        if(abs(el(i)).le.pieni) goto 190
        kz1=kz(i)+1
        goto(10,30,90,50,70,80,120,170,180),kz1
        goto 200
!-----------------------------------------------------------------------
!  DRIFTLENGTH
!-----------------------------------------------------------------------
   10   do 20 l=1,2
          a(i,l,1)=one
          a(i,l,2)=el(i)
          a(i,l,3)=zero
   20   a(i,l,4)=one
        goto 200
!-----------------------------------------------------------------------
!  RECTANGULAR MAGNET
!  HORIZONTAL
!-----------------------------------------------------------------------
   30   ih=1
   40   fok=el(i)*ed(i)/dpsq
        if(abs(fok).le.pieni) goto 10
        rho=(one/ed(i))*dpsq
+if crlibm
        si=sin_rn(fok)
+ei
+if .not.crlibm
        si=sin(fok)
+ei
+if crlibm
        co=cos_rn(fok)
+ei
+if .not.crlibm
        co=cos(fok)
+ei
        a(i,ih,1)=one
        a(i,ih,2)=rho*si
        a(i,ih,3)=zero
        a(i,ih,4)=one
!hr06   a(i,ih,5)=-rho*(one-co)/dpsq
        a(i,ih,5)=((-1d0*rho)*(one-co))/dpsq                             !hr06
+if crlibm
!hr06   a(i,ih,6)=-two*tan_rn(fok*half)/dpsq
        a(i,ih,6)=((-1d0*two)*tan_rn(fok*half))/dpsq                     !hr06
+ei
+if .not.crlibm
!hr06   a(i,ih,6)=-two*tan(fok*half)/dpsq
        a(i,ih,6)=((-1d0*two)*tan(fok*half))/dpsq                        !hr06
+ei
!--VERTIKAL
        ih=ih+1
        if(ih.gt.2) ih=1
+if crlibm
        g=tan_rn(fok*half)/rho
+ei
+if .not.crlibm
        g=tan(fok*half)/rho
+ei
        gl=el(i)*g
        a(i,ih,1)=one-gl
        a(i,ih,2)=el(i)
!hr06   a(i,ih,3)=-g*(two-gl)
        a(i,ih,3)=(-1d0*g)*(two-gl)                                      !hr06
        a(i,ih,4)=a(i,ih,1)
        goto 200
!-----------------------------------------------------------------------
!  SEKTORMAGNET
!  HORIZONTAL
!-----------------------------------------------------------------------
   50   ih=1
   60   fok=el(i)*ed(i)/dpsq
        if(abs(fok).le.pieni) goto 10
        rho=(one/ed(i))*dpsq
+if crlibm
        si=sin_rn(fok)
+ei
+if .not.crlibm
        si=sin(fok)
+ei
+if crlibm
        co=cos_rn(fok)
+ei
+if .not.crlibm
        co=cos(fok)
+ei
        a(i,ih,1)=co
        a(i,ih,2)=rho*si
!hr06   a(i,ih,3)=-si/rho
        a(i,ih,3)=(-1d0*si)/rho                                          !hr06
        a(i,ih,4)=co
!hr06   a(i,ih,5)=-rho*(one-co)/dpsq
        a(i,ih,5)=((-1d0*rho)*(one-co))/dpsq                             !hr06
!hr06   a(i,ih,6)=-si/dpsq
        a(i,ih,6)=(-1d0*si)/dpsq                                         !hr06
!--VERTIKAL
        ih=ih+1
        if(ih.gt.2) ih=1
        a(i,ih,1)=one
        a(i,ih,2)=el(i)
        a(i,ih,3)=zero
        a(i,ih,4)=one
        goto 200
!-----------------------------------------------------------------------
!  RECTANGULAR MAGNET VERTIKAL
!-----------------------------------------------------------------------
   70   ih=2
        goto 40
!-----------------------------------------------------------------------
!  SEKTORMAGNET VERTIKAL
!-----------------------------------------------------------------------
   80   ih=2
        goto 60
!-----------------------------------------------------------------------
!  QUADRUPOLE
!  FOCUSSING
!-----------------------------------------------------------------------
   90   fok=ek(i)/(one+dpp)
        if(abs(fok).le.pieni) goto 10
        ih=0
        hi=sqrt(abs(fok))
        fi=el(i)*hi
        if(fok.gt.zero) goto 110
  100   ih=ih+1
+if crlibm
        a(i,ih,1)=cos_rn(fi)
+ei
+if .not.crlibm
        a(i,ih,1)=cos(fi)
+ei
+if crlibm
        hi1=sin_rn(fi)
+ei
+if .not.crlibm
        hi1=sin(fi)
+ei
        a(i,ih,2)=hi1/hi
!hr06   a(i,ih,3)=-hi1*hi
        a(i,ih,3)=(-1d0*hi1)*hi                                          !hr06
        a(i,ih,4)=a(i,ih,1)
        if(ih.eq.2) goto 200
!--DEFOCUSSING
  110   ih=ih+1
+if crlibm
        hp=exp_rn(fi)
+ei
+if .not.crlibm
        hp=exp(fi)
+ei
        hm=one/hp
        hc=(hp+hm)*half
        hs=(hp-hm)*half
        a(i,ih,1)=hc
        a(i,ih,2)=hs/hi
        a(i,ih,3)=hs*hi
        a(i,ih,4)=hc
        if(ih.eq.1) goto 100
        goto 200
!-----------------------------------------------------------------------
!  COMBINED FUNCTION MAGNET HORIZONTAL
!  FOCUSSING
!-----------------------------------------------------------------------
  120   ih=0
        fokq=ek(i)
  130   wf=ed(i)/dpsq
!hr06   fok=fokq/dpd-wf*wf
        fok=fokq/dpd-wf**2                                               !hr06
        if(abs(fok).le.pieni) goto 10
        afok=abs(fok)
        hi=sqrt(afok)
        fi=hi*el(i)
        if(fok.gt.zero) goto 160
  140   ih=ih+1
+if crlibm
        si=sin_rn(fi)
+ei
+if .not.crlibm
        si=sin(fi)
+ei
+if crlibm
        co=cos_rn(fi)
+ei
+if .not.crlibm
        co=cos(fi)
+ei
        a(i,ih,1)=co
        a(i,ih,2)=si/hi
!hr06   a(i,ih,3)=-si*hi
        a(i,ih,3)=(-1d0*si)*hi                                           !hr06
        a(i,ih,4)=co
!hr06   a(i,ih,5)=-wf/afok*(one-co)/dpsq
        a(i,ih,5)=(((-1d0*wf)/afok)*(one-co))/dpsq                       !hr06
!hr06   a(i,ih,6)=-wf/hi*si/dpsq
        a(i,ih,6)=(((-1d0*wf)/hi)*si)/dpsq                               !hr06
        ih=ih+1
        if(ih.gt.2) ih=1
        hi=sqrt(abs(ek(i)/dpd))
        fi=hi*el(i)
+if crlibm
        hp=exp_rn(fi)
+ei
+if .not.crlibm
        hp=exp(fi)
+ei
        hm=one/hp
        hc=(hp+hm)*half
        hs=(hp-hm)*half
        a(i,ih,1)=hc
        a(i,ih,2)=el(i)
        if(abs(hi).le.pieni) goto 150
        a(i,ih,2)=hs/hi
  150   a(i,ih,3)=hs*hi
        a(i,ih,4)=hc
        goto 200
!--DEFOCUSSING
  160   ih=ih+1
+if crlibm
        hp=exp_rn(fi)
+ei
+if .not.crlibm
        hp=exp(fi)
+ei
        hm=one/hp
        hc=(hp+hm)*half
        hs=(hp-hm)*half
        a(i,ih,1)=hc
        a(i,ih,2)=hs/hi
        a(i,ih,3)=hs*hi
        a(i,ih,4)=hc
!hr06   a(i,ih,5)= wf/afok*(one-hc)/dpsq
        a(i,ih,5)= ((wf/afok)*(one-hc))/dpsq                             !hr06
!hr06   a(i,ih,6)=-wf/hi*hs/dpsq
        a(i,ih,6)=(((-1d0*wf)/hi)*hs)/dpsq                               !hr06
        ih=ih+1
        if(ih.gt.2) ih=1
        hi=sqrt(abs(ek(i)/dpd))
        fi=hi*el(i)
+if crlibm
        si=sin_rn(fi)
+ei
+if .not.crlibm
        si=sin(fi)
+ei
+if crlibm
        co=cos_rn(fi)
+ei
+if .not.crlibm
        co=cos(fi)
+ei
        a(i,ih,1)=co
        a(i,ih,2)=si/hi
!hr06   a(i,ih,3)=-si*hi
        a(i,ih,3)=(-1d0*si)*hi                                           !hr06
        a(i,ih,4)=co
        goto 200
!-----------------------------------------------------------------------
!  COMBINED FUNCTION MAGNET VERTICAL
!-----------------------------------------------------------------------
  170   ih=1
!hr06   fokq=-ek(i)
        fokq=-1d0*ek(i)                                                  !hr06
        goto 130
!-----------------------------------------------------------------------
!  EDGE FOCUSSING
!-----------------------------------------------------------------------
  180   rhoi=ed(i)/dpsq
+if crlibm
!hr06   fok=rhoi*tan_rn(el(i)*rhoi*half)
        fok=rhoi*tan_rn((el(i)*rhoi)*half)                               !hr06
+ei
+if .not.crlibm
!hr06   fok=rhoi*tan(el(i)*rhoi*half)
        fok=rhoi*tan((el(i)*rhoi)*half)                                  !hr06
+ei
        a(i,1,1)=one
        a(i,1,2)=zero
        a(i,1,3)=fok
        a(i,1,4)=one
        a(i,2,1)=one
        a(i,2,2)=zero
!hr06   a(i,2,3)=-fok
        a(i,2,3)=-1d0*fok                                                !hr06
        a(i,2,4)=one
        goto 200
!-----------------------------------------------------------------------
!   NONLINEAR INSERTION
!-----------------------------------------------------------------------
  190   sm(i)=ed(i)
  200 continue
      call block
      return
      end
