+dk subre
      subroutine subre(dpp)
!-----------------------------------------------------------------------
!  CALCULATION OF RESONANCE- AND SUBRESONANCE-DRIVINGTERMS
!-----------------------------------------------------------------------
      implicit none
+if cr
+ca crcoall
+ei
+if crlibm
+ca crlibco
+ei
      integer i,ii,ik,im,ip,ipc,ipcc,ipl,ium,iv,ix,izu,j,jj,jk,jm,k,    &
     &k1,kpz,kzz,l,l1,l2,ll,lmin,min1,min2,mis,mm,mpe,mx,n2,n22,n2e,nf1,&
     &nf3,nf4,nkk,nmz,nn1,nn2,nnf,np,np2,nph,nr,ns,ntx,nv,nv1,nv11,nv2, &
     &nv21,nz2,dj
      double precision aa,ab1,ab2,alfa,b,b1,b2,bb,benkr,beta,btc,bts,cc,&
     &chy,ci,cikve,clo0,clop0,cr,crkve,cxzi,cxzr,cxzyi,cxzyr,cxzyrr,del,&
     &dfac,dphi,dpp,dpp1,dppi,dpr,dt,dtu,dtup,dyy1,dyy2,e,ea,eb,ekk,    &
     &ekko,ep,etl,gerad,gtu1,gtu2,phi,phibf,phy,pie,puf,qu,qv,qw,qwc,r0,&
     &r0a,radi,rc,re,re1,res,rn2,rs,sb1,sb2,sdel,sdel2,sea,seb,shy,ss,t,&
     &vdt1,vdt2,vdt3,vdt4,xl,xs,zl,zs,quz,qvz
+if tilt
      double precision dyy11,qu1,tiltck,tiltck1,tiltck2,tiltck3,tiltck4,&
     &tiltck5,tiltck6,tiltck8,tiltck10,tiltckuk,tiltsk,tiltsk1,tiltsk2, &
     &tiltsk3,tiltsk4,tiltsk5,tiltsk6,tiltsk8,tiltsk10
+ei
+ca parpro
+ca parnum
+ca common
+ca commons
+ca commont1
      dimension t(6,4)
      dimension beta(2),alfa(2),phi(2),phibf(2)
      dimension clo0(2),clop0(2)
      dimension aa(mmul),bb(mmul)
      dimension qw(2),qwc(3),dpr(6)
      dimension nnf(10),ep(2)
      dimension ab1(10),ab2(10),re(10,18),ip(10,18)
      dimension b(10,10),nz2(9),e(10,10)
      dimension chy(9,18),shy(9,18)
      dimension dfac(10),dtu(2,5),dtup(2,5,0:4,0:4)
      dimension cr(mmul),ci(mmul)
+if bnlelens
+ca rhicelens
+ei
+ca save
!-----------------------------------------------------------------------
      ium=5
      ipl=1
      gtu1=zero
      gtu2=zero
      dfac(1)=one
      dfac(2)=one
      dfac(3)=two
!hr13 dfac(4)=6
      dfac(4)=6d0                                                        !hr13
!hr13 dfac(5)=24
      dfac(5)=24d0                                                       !hr13
!hr13 dfac(6)=120
      dfac(6)=120d0                                                      !hr13
!hr13 dfac(7)=720
      dfac(7)=720d0                                                      !hr13
!hr13 dfac(8)=5040
      dfac(8)=5040d0                                                     !hr13
!hr13 dfac(9)=40320
      dfac(9)=40320d0                                                    !hr13
!hr13 dfac(10)=362880
      dfac(10)=362880d0                                                  !hr13
      if(ipt.eq.1) ipl=3
      do 940 ipcc=1,ipl
        ipc=ipcc-ipl+1
        if(ipt.eq.0) ipc=0
        btc=zero
        bts=zero
        phy=zero
        dt=zero
        del=zero
        ns=0
        ik=0
        do 10 i=1,ium
          dpr(i)=zero
   10   continue
        do 20 i=1,ium
          do 20 j=1,4
            t(i,j)=zero
   20   continue
        do 30 i=1,2
          beta(i)=zero
          alfa(i)=zero
          phi(i)=zero
          phibf(i)=zero
          qw(i)=zero
          qwc(i)=zero
          clo0(i)=zero
          clop0(i)=zero
          ep(i)=zero
   30   continue
        qwc(3)=zero
        do 40 i=1,10
          nnf(i)=0
          do 40 j=1,18
            re(i,j)=zero
            ip(i,j)=0
   40   continue
        do 50 i=1,mmul
          aa(i)=zero
          bb(i)=zero
          cr(i)=zero
          ci(i)=zero
   50   continue
        do 60 i=1,2
          do 60 j=1,5
            dtu(i,j)=zero
   60   continue
        do 70 i=1,5
          do 70 j=0,4
            do 70 k=0,4
              dtup(1,i,j,k)=zero
              dtup(2,i,j,k)=zero
   70   continue
        do 120 i=1,9
          nz2(i)=0
          do 110 j=1,18
            chy(i,j)=zero
            shy(i,j)=zero
            do 100 k=1,10
              do 80 ii=1,10
                e(k,ii)=zero
                b(k,ii)=zero
   80         continue
              do 90 l=1,5
                rtc(i,j,k,l)=zero
                rts(i,j,k,l)=zero
   90         continue
  100       continue
  110     continue
  120   continue
+if cr
        write(lout,10030)
+ei
+if .not.cr
        write(*,10030)
+ei
+if cr
        write(lout,10020)
+ei
+if .not.cr
        write(*,10020)
+ei
        pie=two*pi
        etl=zero
        radi=totl/pie
        nr=0
        dpr(1)=dpp*c1e3
        dpr(6)=c1e3
        dpp1=dpp+ded
        call clorb(dpp1)
        do 130 l=1,2
          clo0(l)=clo(l)
  130   clop0(l)=clop(l)
        call clorb(dpp)
        do 140 l=1,2
          di0(l)=(clo0(l)-clo(l))/ded
  140   dip0(l)=(clop0(l)-clop(l))/ded
+if cr
        write(lout,10030)
+ei
+if .not.cr
        write(*,10030)
+ei
+if cr
        write(lout,10120) (di0(l),dip0(l),l=1,2)
+ei
+if .not.cr
        write(*,10120) (di0(l),dip0(l),l=1,2)
+ei
        call betalf(dpp,qw)
        call phasad(dpp,qwc)
        if(ierro.ne.0) call prror(22+ierro)
+if cr
        write(lout,10070) dpp,qwc(1),qwc(2)
+ei
+if .not.cr
        write(*,10070) dpp,qwc(1),qwc(2)
+ei
        call envar(dpp)
!--STARTVALUES OF THE TRAJECTORIES
        do 150 l=1,2
          ll=2*l
          alfa(l)=alf0(l)
          beta(l)=bet0(l)
          t(1,ll-1)=clo(l)
          t(1,ll)=clop(l)
          clo0(l)=clo(l)
  150   clop0(l)=clop(l)
        do 160 i=1,4
          do 160 j=1,4
            t(i+1,j)=ta(j,i)
  160   t(i+1,j)=ta(j,i)
+if cr
        write(lout,10030)
+ei
+if .not.cr
        write(*,10030)
+ei
+if cr
        write(lout,10040)
+ei
+if .not.cr
        write(*,10040)
+ei
+if cr
        write(lout,10030)
+ei
+if .not.cr
        write(*,10030)
+ei
+if cr
        write(lout,10010) nr,'START   ',zero,zero,(beta(l),alfa(l),     &
     &phi(l),                                                           &
+ei
+if .not.cr
        write(*,10010) nr,'START   ',zero,zero,(beta(l),alfa(l),phi(l), &
+ei
     &di0(l),dip0(l),clo0(l),clop0(l),l=1,2)
!--EP=EMITTANCE IN PI*MM*MRAD
!hr06   ep(1)=tam1*tam1/beta(1)
        ep(1)=tam1**2/beta(1)                                            !hr06
!hr06   ep(2)=tam2*tam2/beta(2)
        ep(2)=tam2**2/beta(2)                                            !hr06
+if cr
        write(lout,10050) tam1,ep(1),tam2,ep(2)
+ei
+if .not.cr
        write(*,10050) tam1,ep(1),tam2,ep(2)
+ei
+if cr
        write(lout,10030)
+ei
+if .not.cr
        write(*,10030)
+ei
!--SINGLE TURN BLOCKLOOP
        izu=0
        do 790 k=1,iu
          do 170 k1=1,10
            ab1(k1)=zero
  170     ab2(k1)=zero
          ix=ic(k)
          if(ix.gt.nblo) goto 250
          jj=0
          dj=1
          if(ix.gt.0) goto 180
!hr13     ix=-ix
          ix=-1*ix
          jj=mel(ix)+1
          dj=-1
  180     jm=mel(ix)
!--SINGLE TURN BLOCKLOOP
          do 240 j=1,jm
            jj=jj+dj
            jk=mtyp(ix,jj)
            if(ithick.eq.1.and.kz(jk).ne.0) goto 210
            if(ithick.eq.0.and.kz(jk).ne.0) goto 790
!--PURE DRIFTLENGTH
            etl=etl+el(jk)
            do 190 l=1,2
              ll=2*l
              if(abs(t(ll,ll-1)).gt.pieni) then
+if crlibm
                phibf(l)=atan_rn(t(ll+1,ll-1)/t(ll,ll-1))
+ei
+if .not.crlibm
                phibf(l)=atan(t(ll+1,ll-1)/t(ll,ll-1))
+ei
              else
                phibf(l)=pi2
              endif
              do 190 i=1,ium
  190       t(i,ll-1)=t(i,ll-1)+t(i,ll)*(el(jk))
            do 200 l=1,2
              ll=2*l
!hr06         beta(l)=t(ll,ll-1)*t(ll,ll-1)+t(ll+1,ll-1)*t(ll+1,ll-1)
              beta(l)=t(ll,ll-1)**2+t(ll+1,ll-1)**2                      !hr06
!hr06         alfa(l)=-(t(ll,ll-1)*t(ll,ll)+t(ll+1,ll-1)*t(ll+1,ll))
              alfa(l)=-1d0*(t(ll,ll-1)*t(ll,ll)+t(ll+1,ll-1)*t(ll+1,ll)) !hr06
              clo0(l)=t(1,ll-1)
              clop0(l)=t(1,ll)
              if(abs(t(ll,ll-1)).gt.pieni) then
+if crlibm
                dphi=atan_rn(t(ll+1,ll-1)/t(ll,ll-1))-phibf(l)
+ei
+if .not.crlibm
                dphi=atan(t(ll+1,ll-1)/t(ll,ll-1))-phibf(l)
+ei
              else
                dphi=pi2-phibf(l)
              endif
!hr06         if(-dphi.gt.pieni) dphi=dphi+pi
              if(-1d0*dphi.gt.pieni) dphi=dphi+pi                        !hr06
  200       phi(l)=phi(l)+dphi/pie
            nr=nr+1
            goto 240
!--MAGNETELEMENT
  210       continue
            if(kz(jk).ne.8) etl=etl+el(jk)
            do l=1,2
              ll=2*l
              if(abs(t(ll,ll-1)).gt.pieni) then
+if crlibm
                phibf(l)=atan_rn(t(ll+1,ll-1)/t(ll,ll-1))
+ei
+if .not.crlibm
                phibf(l)=atan(t(ll+1,ll-1)/t(ll,ll-1))
+ei
              else
                phibf(l)=zero
              endif
              do i=1,ium
                puf=t(i,ll-1)
!hr06           t(i,ll-1)=puf*a(jk,l,1)+t(i,ll)*a(jk,l,2)+dpr(i)*a      &
                t(i,ll-1)=(puf*a(jk,l,1)+t(i,ll)*a(jk,l,2))+dpr(i)*a    &!hr06
     &(jk,l,5)
!hr06           t(i,ll)=puf*a(jk,l,3)+t(i,ll)*a(jk,l,4)+dpr(i)*a(jk,l,6)
              t(i,ll)=(puf*a(jk,l,3)+t(i,ll)*a(jk,l,4))+dpr(i)*a(jk,l,6) !hr06
              enddo
            enddo
            do l=1,2
              ll=2*l
!hr06         beta(l)=t(ll,ll-1)*t(ll,ll-1)+t(ll+1,ll-1)*t(ll+1,ll-1)
              beta(l)=t(ll,ll-1)**2+t(ll+1,ll-1)**2                      !hr06
!hr06         alfa(l)=-(t(ll,ll-1)*t(ll,ll)+t(ll+1,ll-1)*t(ll+1,ll))
              alfa(l)=-1d0*(t(ll,ll-1)*t(ll,ll)+t(ll+1,ll-1)*t(ll+1,ll)) !hr06
              clo0(l)=t(1,ll-1)
              clop0(l)=t(1,ll)
              if(abs(t(ll,ll-1)).gt.pieni) then
+if crlibm
                dphi=atan_rn(t(ll+1,ll-1)/t(ll,ll-1))-phibf(l)
+ei
+if .not.crlibm
                dphi=atan(t(ll+1,ll-1)/t(ll,ll-1))-phibf(l)
+ei
              else
                dphi=-phibf(l)
              endif
              if(kz(jk).ne.8.and.-dphi.gt.pieni) dphi=dphi+pi
              phi(l)=phi(l)+dphi/pie
            enddo
            nr=nr+1
  240     continue
          goto 790
!--NL-INSERTION
  250     ix=ix-nblo
          qu=zero
          qv=zero
