      phi=param(ne,1)
      nsli=param(ne,2)
      alpha=param(ne,3)
      phi2=param(ne,5)
!hr06 f=param(ne,4)/nsli
      f=param(ne,4)/dble(nsli)                                           !hr06
+if crlibm
      sphi=sin_rn(phi)
      sphi2=sin_rn(phi2)
+ei
+if .not.crlibm
      sphi=sin(phi)
      sphi2=sin(phi2)
+ei
+if crlibm
      cphi=cos_rn(phi)
      cphi2=cos_rn(phi2)
+ei
+if .not.crlibm
      cphi=cos(phi)
      cphi2=cos(phi2)
