!hr07       s21  =  - b(i,l)
            s21  =  -1d0* dble(b(i,l))                                   !hr07
            do 231   j  =  1, im1
               s21  =  dotf(a(i,j),b(j,l),s21)
 231           continue
!hr07       b(i,l)  =  - a(i,i)*s21
            b(i,l)  =  real((-1d0*dble(a(i,i)))*s21)                     !hr07
 232        continue
         nm1  =  n-1
