      double precision function ran_gauss(cut)
!*********************************************************************
!
! RAN_GAUSS - will generate a normal distribution from a uniform
!   distribution between [0,1].
!   See "Communications of the ACM", V. 15 (1972), p. 873.
!
! cut - double precision - cut for distribution in units of sigma
!                the cut must be greater than 0.5
!
!*********************************************************************
      implicit none
+if cr
+ca crcoall
+ei
+if crlibm
+ca crlibco
+ei

      logical flag
      DATA flag/.TRUE./
      real rndm4
      double precision x, u1, u2, twopi, r,cut
+ca save
+if automatc
      write(*,*) "ERROR in ran_gauss"
      write(*,*) "Please review use of save block in this algorithm!"
      write(*,*) "u1 and u2 should always be saved, and flag "//
     &     "is automatically so due to DATA statement"
      exit(1)
+ei
      
+if crlibm
            twopi=8d0*atan_rn(1d0) !Why not 2*pi, where pi is in block "common"?
+ei
+if .not.crlibm
            twopi=8d0*atan(1d0)
+ei
    1       if (flag) then
              r = dble(rndm4( ))
              r = max(r, 0.5d0**32)
              r = min(r, 1d0-0.5d0**32)
+if crlibm
              u1 = sqrt(-2d0*log_rn( r ))
+ei
+if .not.crlibm
              u1 = sqrt(-2d0*log( r ))
+ei
              u2 = dble(rndm4( ))
+if crlibm
              x = u1 * cos_rn(twopi*u2)
+ei
+if .not.crlibm
              x = u1 * cos(twopi*u2)
+ei
            else
+if crlibm
              x = u1 * sin_rn(twopi*u2)
+ei
+if .not.crlibm
              x = u1 * sin(twopi*u2)
+ei
            endif

          flag = .not. flag

!  cut the distribution if cut > 0.5
          if (cut .gt. 0.5d0 .and. abs(x) .gt. cut) goto 1

          ran_gauss = x
        return
      end
